(276, 'Ejemplo de formulario Prestashop ', 'BOOTSTRAP', '<form id="customer_form" class="defaultForm form-horizontal AdminCustomers" novalidate="" enctype="multipart/form-data" method="post" action="index.php?controller=AdminCustomers&token=445c2b1b0f1e8e9fd5ca384a8c7fe67a">\r\n<input type="hidden" value="1" name="submitAddcustomer">\r\n<div id="fieldset_0" class="panel">\r\n<div class="panel-heading">\r\n<i class="icon-user"></i>\r\nCustomer\r\n</div>\r\n<div class="form-wrapper">\r\n<div class="form-group">\r\n<label class="control-label col-lg-3"> Social title </label>\r\n<div class="col-lg-9 ">\r\n<div class="radio t">\r\n<label>\r\n<input id="gender_1" type="radio" value="1" name="id_gender">\r\nSr.\r\n</label>\r\n</div>\r\n<div class="radio t">\r\n<label>\r\n<input id="gender_2" type="radio" value="2" name="id_gender">\r\nSra.\r\n</label>\r\n</div>\r\n</div>\r\n</div>\r\n<div class="form-group">\r\n<label class="control-label col-lg-3 required">\r\n<span class="label-tooltip" title="" data-html="true" data-toggle="tooltip" data-original-title="	Invalid characters: 0-9!&lt;&gt;,;?=+()@#"°{}_$%: "> First name </span>\r\n</label>\r\n<div class="col-lg-4 ">\r\n<input id="firstname" class="" type="text" required="required" value="" name="firstname">\r\n</div>\r\n</div>\r\n<div class="form-group">\r\n<label class="control-label col-lg-3 required">\r\n<span class="label-tooltip" title="" data-html="true" data-toggle="tooltip" data-original-title="	Invalid characters: 0-9!&lt;&gt;,;?=+()@#"°{}_$%: "> Last name </span>\r\n</label>\r\n<div class="col-lg-4 ">\r\n<input id="lastname" class="" type="text" required="required" value="" name="lastname">\r\n</div>\r\n</div>\r\n<div class="form-group">\r\n<label class="control-label col-lg-3 required"> Email address </label>\r\n<div class="col-lg-4 ">\r\n<div class="input-group">\r\n<span class="input-group-addon">\r\n<i class="icon-envelope-o"></i>\r\n</span>\r\n<input id="email" class="" type="text" required="required" autocomplete="off" value="" name="email">\r\n</div>\r\n</div>\r\n</div>\r\n<div class="form-group">\r\n<label class="control-label col-lg-3 required">\r\n<span class="label-tooltip" title="" data-html="true" data-toggle="tooltip" data-original-title="	Password should be at least 5 characters long. "> Password </span>\r\n</label>\r\n<div class="col-lg-4 ">\r\n<div class="input-group fixed-width-lg">\r\n<span class="input-group-addon">\r\n<i class="icon-key"></i>\r\n</span>\r\n<input id="passwd" class="" type="password" required="required" value="" name="passwd">\r\n</div>\r\n</div>\r\n</div>\r\n<div class="form-group">\r\n<label class="control-label col-lg-3"> Birthday </label>\r\n<div class="col-lg-9 ">\r\n<div class="form-group">\r\n<div class="col-lg-2">\r\n<select class="fixed-width-lg" name="days">\r\n<option value="">-</option>\r\n<option value="1">1</option>\r\n<option value="2">2</option>\r\n<option value="3">3</option>\r\n<option value="4">4</option>\r\n<option value="5">5</option>\r\n<option value="6">6</option>\r\n<option value="7">7</option>\r\n<option value="8">8</option>\r\n<option value="9">9</option>\r\n<option value="10">10</option>\r\n<option value="11">11</option>\r\n<option value="12">12</option>\r\n<option value="13">13</option>\r\n<option value="14">14</option>\r\n<option value="15">15</option>\r\n<option value="16">16</option>\r\n<option value="17">17</option>\r\n<option value="18">18</option>\r\n<option value="19">19</option>\r\n<option value="20">20</option>\r\n<option value="21">21</option>\r\n<option value="22">22</option>\r\n<option value="23">23</option>\r\n<option value="24">24</option>\r\n<option value="25">25</option>\r\n<option value="26">26</option>\r\n<option value="27">27</option>\r\n<option value="28">28</option>\r\n<option value="29">29</option>\r\n<option value="30">30</option>\r\n<option value="31">31</option>\r\n</select>\r\n</div>\r\n<div class="col-lg-2">\r\n<select class="fixed-width-lg" name="months">\r\n<option value="">-</opti