lue="1978">1978</option>\r\n<option value="1977">1977</option>\r\n<option value="1976">1976</option>\r\n<option value="1975">1975</option>\r\n<option value="1974">1974</option>\r\n<option value="1973">1973</option>\r\n<option value="1972">1972</option>\r\n<option value="1971">1971</option>\r\n<option value="1970">1970</option>\r\n<option value="1969">1969</option>\r\n<option value="1968">1968</option>\r\n<option value="1967">1967</option>\r\n<option value="1966">1966</option>\r\n<option value="1965">1965</option>\r\n<option value="1964">1964</option>\r\n<option value="1963">1963</option>\r\n<option value="1962">1962</option>\r\n<option value="1961">1961</option>\r\n<option value="1960">1960</option>\r\n<option value="1959">1959</option>\r\n<option value="1958">1958</option>\r\n<option value="1957">1957</option>\r\n<option value="1956">1956</option>\r\n<option value="1955">1955</option>\r\n<option value="1954">1954</option>\r\n<option value="1953">1953</option>\r\n<option value="1952">1952</option>\r\n<option value="1951">1951</option>\r\n<option value="1950">1950</option>\r\n<option value="1949">1949</option>\r\n<option value="1948">1948</option>\r\n<option value="1947">1947</option>\r\n<option value="1946">1946</option>\r\n<option value="1945">1945</option>\r\n<option value="1944">1944</option>\r\n<option value="1943">1943</option>\r\n<option value="1942">1942</option>\r\n<option value="1941">1941</option>\r\n<option value="1940">1940</option>\r\n<option value="1939">1939</option>\r\n<option value="1938">1938</option>\r\n<option value="1937">1937</option>\r\n<option value="1936">1936</option>\r\n<option value="1935">1935</option>\r\n<option value="1934">1934</option>\r\n<option value="1933">1933</option>\r\n<option value="1932">1932</option>\r\n<option value="1931">1931</option>\r\n<option value="1930">1930</option>\r\n<option value="1929">1929</option>\r\n<option value="1928">1928</option>\r\n<option value="1927">1927</option>\r\n<option value="1926">1926</option>\r\n<option value="1925">1925</option>\r\n<option value="1924">1924</option>\r\n<option value="1923">1923</option>\r\n<option value="1922">1922</option>\r\n<option value="1921">1921</option>\r\n<option value="1920">1920</option>\r\n<option value="1919">1919</option>\r\n<option value="1918">1918</option>\r\n<option value="1917">1917</option>\r\n<option value="1916">1916</option>\r\n<option value="1915">1915</option>\r\n<option value="1914">1914</option>\r\n<option value="1913">1913</option>\r\n<option value="1912">1912</option>\r\n<option value="1911">1911</option>\r\n<option value="1910">1910</option>\r\n<option value="1909">1909</option>\r\n<option value="1908">1908</option>\r\n<option value="1907">1907</option>\r\n<option value="1906">1906</option>\r\n<option value="1905">1905</option>\r\n<option value="1904">1904</option>\r\n<option value="1903">1903</option>\r\n<option value="1902">1902</option>\r\n<option value="1901">1901</option>\r\n<option value="1900">1900</option>\r\n</select>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<div class="form-group">\r\n<label class="control-label col-lg-3">\r\n<span class="label-tooltip" title="" data-html="true" data-toggle="tooltip" data-original-title="	Enable or disable customer login. "> Enabled </span>\r\n</label>\r\n<div class="col-lg-9 ">\r\n<span class="switch prestashop-switch fixed-width-lg">\r\n<input id="active_on" type="radio" checked="checked" value="1" name="active">\r\n<label for="active_on">Yes</label>\r\n<input id="active_off" type="radio" value="0" name="active">\r\n<label for="active_off">No</label>\r\n<a class="slide-button btn"></a>\r\n</span>\r\n</div>\r\n</div>\r\n<div class="form-group">\r\n<label class="control-label col-lg-3">\r\n<span class="label-tooltip" title="" data-html="true" data-toggle="tooltip" data-original-title="	This customer will receive your newsletter via email. "> Newsletter </span>\r\n</label>\r\n<div class="col-lg-9 ">\r\n<span class="switch prestashop-switch fixed-width-lg">\r\n<input id="newsletter_on" type="radio" value="1" name="newsletter">\r\n<label for="newsletter_on">Yes</label>\r\n<input id="newsletter_off" type="radio" checked="checked" value="0" name="newsletter">\r\n<label for="newsletter_off">No</label>\r\n<a class="slide-button btn"></a>\r\n</span>\r\n</div>\r\n</div>\r\n<div class="form-group">\r\n<label class="control-label col-lg-3">\r\n<span class="label-tooltip" title="" data-html="true" data-toggle="tooltip" data-original-title="	This customer will receive your ads via email. "> Opt-in </span>\r\n</label>\r\n<div class="col-lg-9 ">\r\n<span class="switch prestashop-switch fixed-width-lg">\r\n<input id="optin_on" type="radio" value="1" name="optin">\r\n<label for="optin_on">Yes</label>\r\n<input id="optin_off" type="radio" checked="checked" value="0" name="optin">\r\n<label for="optin_off">No</label>\r\n<a class="slide-button btn"></a>\r\n</span>\r\n</div>\r\n</div>\r\n<div class="form-group">\r\n<label class="control-label col-lg-3 required">\r\n<span class="label-tooltip" title="" data-html="true" data-toggle="tooltip" data-original-title="	Select all the groups that you would like to apply to this customer. "> Group access </span>\r\n</label>\r\n<div class="col-lg-6 ">\r\n<div class="row">\r\n<div class="col-lg-6">\r\n<table class="table table-bordered">\r\n<thead>\r\n<tr>\r\n<th class="fixed-width-xs">\r\n<span class="title_box">\r\n<input id="checkme" type="checkbox" onclick="checkDelBoxes(this.form, ''groupBox[]'', this.checked)" name="checkme">\r\n</span>\r\n</th>\r\n<th class="fixed-width-xs">\r\n<span class="title_box">ID</span>\r\n</th>\r\n<th>\r\n<span class="title_box"> Group name </span>\r\n</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n</table>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<div class="form-group">\r\n<label class="control-label col-lg-3">\r\n<span class="label-tooltip" title="" data-html="true" data-toggle="tooltip" data-original-title="	This group will be the user&#039;s default group. Only the discount for the selected group will be applied to this customer. "> Default customer group </span>\r\n</label>\r\n<div class="col-lg-4 ">\r\n<select id="id_default_group" class=" fixed-width-xl" name="id_default_group">\r\n<option value="1">Visitor</option>\r\n<option value="2">Guest</option>\r\n<option selected="selected" value="3">Customer</option>\r\n</select>\r\n</div>\r\n</div>\r\n</div>\r\n<div class="panel-footer">\r\n<button id="customer_form_submit_btn" class="btn btn-default pull-right" name="submitAddcustomer" value="1" type="submit">\r\n<i class="process-icon-save"></i>\r\nSave\r\n</button>\r\n<a class="btn btn-default" onclick="window.history.back();" href="index.php?controller=AdminCustomers&token=445c2b1b0f1e8e9fd5ca384a8c7fe67a">\r\n<i class="process-icon-cancel"></i>\r\nCancel\r\n</a>\r\n</div>\r\n</div>\r\n</form>', '2015-02-08'),
(277, 'Plantilla Online', 'BOOTSTRAP', '<!DOCTYPE html>\r\n<html lang="es">\r\n<head>\r\n  <meta charset ="UTF8">\r\n  <title>Ejemplo Boostrap</title>\r\n  <meta name="viewport" content="width = device-width">\r\n  <link href="http://getbootstrap.com/dist/css/bootstrap.min.css" rel="stylesheet">\r\n  <script src="https://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js"></script>\r\n  <script src="http://getbootstrap.com/dist/js/bootstrap.min.js"></script>\r\n\r\n  <!-- HTML5 Shim and Respond.js IE8 support of HTML5 elements and media queries -->\r\n  <!--[if lt IE 9]>\r\n      <script src="https://oss.maxcdn.com/libs/html5shiv/3.7.0/html5shiv.js"></script>\r\n      <script src="https://oss.maxcdn.com/libs/respond.js/1.4.2/respond.min.js"></script>\r\n      <![endif]-->\r\n\r\n\r\n</head>\r\n <body>\r\n  \r\n  <div class="container">\r\n       <h1>Hello</h1>    \r\n  </div>\r\n       \r\n\r\n </body>\r\n</html>', '2015-02-11'),
(278, 'Boton con ventana modal', 'BOOTSTRAP', '<!-- Button trigger modal -->\r\n<button type="button" class="btn btn-primary btn-lg" data-toggle="modal" data-target="#myModal">\r\n  Launch demo modal\r\n</button>\r\n\r\n<!-- Modal -->\r\n<div class="modal fade" id="myModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true">\r\n  <div class="modal-dialog">\r\n    <div class="modal-content">\r\n      <div class="modal-header">\r\n        <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>\r\n        <h4 class="modal-title" id="myModalLabel">Modal title</h4>\r\n      </div>\r\n      <div class="modal-body">\r\n        ...\r\n      </div>\r\n      <div class="modal-footer">\r\n        <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>\r\n        <button type="button" class="btn btn-primary">Save changes</button>\r\n      </div>\r\n    </div>\r\n  </div>\r\n</div>', '2015-02-08'),
(279, 'Iconos', 'BOOTSTRAP', '<button type="button" class="btn btn-default" aria-label="Left Align">\r\n  <span class="glyphicon glyphicon-align-left" aria-hidden="true"></span>\r\n</button>\r\n\r\n<button type="button" class="btn btn-default btn-lg">\r\n  <span class="glyphicon glyphicon-star" aria-hidden="true"></span> Star\r\n</button>', '2015-02-08'),
(280, 'Login Básico', 'BOOTSTRAP', '<!DOCTYPE html>\r\n<html lang="es">\r\n<head>\r\n  <meta charset ="UTF8">\r\n  <title>Ejemplo Boostrap</title>\r\n  <meta name="viewport" content="width = device-width">\r\n  <link href="http://getbootstrap.com/dist/css/bootstrap.min.css" rel="stylesheet">\r\n  <script src="https://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js"></script>\r\n  <script src="http://getbootstrap.com/dist/js/bootstrap.min.js"></script>\r\n\r\n  <style>\r\n   .form-signin {\r\n    margin-bottom: 0;\r\n    margin-left: auto;\r\n    margin-right: auto;\r\n    margin-top: 0;\r\n    max-width: 330px;\r\n    padding-bottom: 15px;\r\n    padding-left: 15px;\r\n    padding-right: 15px;\r\n    padding-top: 15px;\r\n}\r\n\r\n\r\n.form-signin .form-control {\r\n    font-size: 16px;\r\n    height: auto;\r\n    padding-bottom: 10px;\r\n    padding-left: 10px;\r\n    padding-right: 10px;\r\n    padding-top: 10px;\r\n    position: relative;\r\n    margin-top: 10px;\r\n    margin-bottom: 10px;\r\n}\r\n\r\n\r\n  </style>\r\n\r\n</head>\r\n <body>\r\n  <div class="container">\r\n    <form class="form-signin">\r\n      <h2 class="form-signin-heading">Please sign in</h2>\r\n      <label class="sr-only" for="inputEmail">Email address</label>\r\n      <input id="inputEmail" class="form-control" type="email" autofocus="" required="" placeholder="Email address">\r\n      <label class="sr-only" for="inputPassword">Password</label>\r\n      <input id="inputPassword" class="form-control" type="password" required="" placeholder="Password">\r\n      <div class="checkbox">\r\n        <label>\r\n          <input type="checkbox" value="remember-me">\r\n          Remember me\r\n        </label>\r\n      </div>\r\n      <button class="btn btn-lg btn-primary btn-block" type="submit">Sign in</button>\r\n    </form>\r\n  </div>\r\n\r\n </body>\r\n</html>', '2015-02-09'),
(281, 'Style falconMaster', 'CSS', '/*\r\nTheme Name: Nombre del Tema\r\nTheme URI: www.miweb.com\r\nDescription: description del tema\r\nVersion:1\r\nAuthor : auto\r\nAuthor URL: direccion web del autor\r\n*/\r\n\r\n\r\n/*--------Estilos generales-------------------*/\r\n\r\n*{\r\n\r\n	margin: 0px;\r\n	padding: 0px;\r\n}\r\n\r\nbody\r\n{\r\n	background: #191919;\r\n	font-size: 12pt;\r\n	font-family: "open sans, Arial, Helvetica, sans-serif";\r\n\r\n}\r\n\r\n\r\nheader, #main, footer{\r\n 	width:85%;             /*-------- Responsive design -------------------*/\r\n    max-width: 960px;\r\n    margin: 0px auto;\r\n\r\n}\r\n\r\n\r\n\r\n/*-------- Configuramos los vinculos -------------------*/\r\na {\r\n    color:#bf2222;\r\n    /*--- Quitamos el subrayado--*/\r\n    text-decoration: none;\r\n} \r\n\r\na:hover\r\n{\r\n	text-decoration: underline;\r\n}\r\n\r\n/*--------Header----------------*/\r\n\r\nheader {\r\n	margin-top:20px;\r\n	margin-bottom: 20px;\r\n	overflow: hidden;\r\n	width: 85%;\r\n}\r\n\r\nheader #logo {\r\nwidth: 40%;\r\nfloat:left;\r\n\r\n}\r\n\r\n\r\nheader #logo p {\r\n\r\n	font-family:''Raleway'',''Helvetica'',Arial, sans-serif;\r\n	font-size: 4rem;/*-- Tomara el tamaño del Body y lo multiplicara por 4 --*/\r\n	font-weight:100;\r\n	text-align: left;\r\n	color: #fff;\r\n\r\n}\r\n\r\nheader #logo span {\r\n  text-align: center;\r\n  width: 100%;\r\n  display: block;\r\n  position: relative;\r\n  font-family:''Raleway'',''Helvetica'',Arial, sans-serif;\r\n  font-weight:400;\r\n  /* Movemos 20 pixel a la derecha */\r\n  right: 20px;\r\n  margin-bottom: 10px;\r\n  color :#fff;\r\n}\r\n\r\n\r\n/*--------  Configuramos el menu ---------------*/\r\n\r\nheader nav {\r\n	width: 60%; /* Si recuerdas en el logo utilizamos el 40% */\r\n	float:right;\r\n	padding: 10px 0px;\r\n	margin-top: 30px;\r\n}\r\n\r\nheader nav ul {\r\n	float: right;\r\n\r\n}\r\n\r\nheader nav ul li \r\n{\r\nlist-style: none;	\r\ndisplay: inline-block;  /* Con esto los ponemos uno arlado de otro */\r\nmargin-left: 30px; /* Distancia entre elemento */\r\nmargin-bottom: 10px;\r\n}\r\n\r\n\r\n/*--------  Configuramos los textos del menu ---------------*/\r\nheader nav ul li a{\r\n	font-family: ''Raleway'', Helvetica, Arial;\r\n	font-weight: 300;\r\n	font-size: 1.3rem;\r\n	color: #fff;\r\n}\r\n\r\nheader nav ul li a:hover{\r\n	color: #bf2222;\r\n\r\n}\r\n\r\n\r\n/* --------------     MAIN       --------------  */\r\n#main {\r\n	overflow: hidden;	\r\n}\r\n\r\n\r\n\r\n/* -------- Slideshow -----*/\r\n\r\n\r\n#main #slideshow {\r\n	width: 100%;\r\n	float:left;\r\n	overflow: hidden;\r\n	margin-bottom: 20px;\r\n\r\n}\r\n\r\n#main #slideshow .thumb {\r\n   width: 49%;\r\n   float: left;\r\n   margin-right: 2%;\r\n}\r\n\r\n\r\n#main #slideshow article{\r\n	float:left;\r\n	font-family: ''Open-sans'', Arial, Helvetica;\r\n	font-size: 1rem;\r\n	text-align: justify;\r\n	width:49%;\r\n	color: #fff;\r\n}\r\n\r\n\r\n#main #slideshow article h2{\r\n	font-family: ''Raleway'', Helvetica, Arial;\r\n	font-size: 2rem;\r\n	font-weight: 300;\r\n	text-align: left;\r\n\r\n\r\n}\r\n\r\n#main #slideshow article .date{\r\n	font-family: ''Raleway'', Helvetica, Arial;\r\n	font-size: .8rem;\r\n	font-weight: 500;\r\n	margin-bottom: 10px;\r\n	color: #777777;\r\n\r\n}\r\n\r\n\r\n/* -------- Le ponemos los cuadros y quitamos los numeros -----*/\r\n#main #slideshow .slidesjs-pagination li{\r\n	background: #fff;\r\n	width: 20px;\r\n	height: 20px;\r\n	margin-right: 10px;\r\n	display: inline-block;  /* -------- Se posicione uno arlado de otro -----*/\r\n\r\n}\r\n\r\n\r\n\r\n#main #slideshow .slidesjs-pagination a{\r\n	width: 20px; /* -------- Aqui aumentamos el area clicleable-----*/\r\n	height: 20px;\r\n	display: block; /* Sino no funciona */\r\n	font-size: 0px;\r\n}\r\n\r\n\r\n#main #slideshow .slidesjs-pagination li .active{\r\n	background: #333333;\r\n}\r\n\r\n\r\n\r\n\r\n\r\n/* --------------     Configurando la lista de articulos     --------------  */\r\n\r\n#main #article_list {\r\n	clear:both; /* es omo un reset de los estilo de float left que le pusimos al header*/\r\n	width: 75%; /* Porque estamos trabajando con responsive desing */\r\n	float: left; \r\n}\r\n\r\n#main #article_list article{\r\n	margin-bottom: 40px;  /* y separamos los articulos*/\r\n	margin-right: 4%;\r\n	padding-bottom: 40px;\r\n	border-bottom: 1px solid #333333;\r\n	overflow: hidden;\r\n\r\n}\r\n\r\n#main #article_list article .thumb {\r\n    width:35%;\r\n    margin-right: 4%;\r\n    float:left;\r\n    margin-bottom: 2%;\r\n	}\r\n\r\n\r\n\r\n\r\n#main #article_list article h2{\r\n    font-family: ''Raleway'', Helvetica, Arial;\r\n    font-size: 1.5rem;\r\n    font-weight: 300;\r\n\r\n	}\r\n\r\n	#main #article_list article h2 a{\r\n    color: #fff;\r\n\r\n	}\r\n\r\n\r\n   #main #article_list article h2 a:hover{\r\n    text-decoration: none;\r\n    color: #bf2222;\r\n	}\r\n\r\n\r\n\r\n   #main #article_list article .extract{\r\n   	font-family: ''Raleway'', Helvetica, Arial;\r\n    text-align: justify;\r\n    color: #fff;\r\n	}\r\n\r\n\r\n   #main #article_list article .date{\r\n    font-size: 0.8rem;\r\n    color: #777777;\r\n    margin-bottom: 10px;\r\n	}\r\n\r\n\r\n\r\n   #main #article_list #pagination{\r\n    margin-bottom: 40px;\r\n    width:100%;\r\n	}\r\n\r\n\r\n   #main #article_list #pagination p {\r\n   text-align: center;\r\n\r\n   }\r\n\r\n    #main #article_list #pagination a {\r\n    margin: 0px 20px;\r\n    font-size: 1.5rem;\r\n    font-family: ''Raleway'', Helvetica, Arial;\r\n    font-weight: 300px;\r\n    display: inline-block;\r\n\r\n   \r\n   }\r\n\r\n\r\n\r\n\r\n   /* Aside  */\r\n	\r\n\r\n	#main aside  {\r\n     width: 25%;\r\n     float:left;\r\n	}\r\n\r\n\r\n	#main .widget{\r\n			width: 100%;\r\n\r\n	}\r\n\r\n   .widget{\r\n			margin-bottom: 50px;\r\n			display: block;\r\n			float: left;  \r\n\r\n	}\r\n	\r\n\r\n	   .widget h3{\r\n			font-size: 1.4rem;\r\n			font-family: ''Raleway'', Helvetica, Arial;\r\n			font-weight: 400;\r\n			color: #333;\r\n			margin-bottom: 10px;\r\n	}\r\n	\r\n\r\n		   .widget ul li {\r\n			list-style: none;\r\n			margin-bottom: 10px;\r\n\r\n	   }\r\n\r\n\r\n	   		   .widget ul li a{\r\n				 font-family: ''Open-sans'', Helvetica, Arial;\r\n				 font-size: 0.9rem;\r\n				 color: #777;			\r\n	   }\r\n	\r\n\r\n	 .widget ul li a:hover{\r\n	 	color: #bf2222;\r\n	 	text-decoration: none;\r\n\r\n	 }\r\n\r\n\r\n	 /*  ------------------   Footer        ------------------------ */\r\n\r\n\r\n	 footer {\r\n	 	width:85%;\r\n	 }\r\n	\r\n\r\n\r\n	 footer .widget {\r\n	 	width:25%;\r\n	 }\r\n	\r\n\r\n\r\n	#copyright {\r\n		width: 100%;\r\n		clear: both; /* Pasa para abajo */\r\n		text-align: center;\r\n		padding: 10px;\r\n		color: #333;\r\n		font-family: ''Raleway'', Helvetica, Arial;\r\n		font-size: 0.9rem;\r\n	}', '2015-02-09'),
(282, 'Alerta', 'BOOTSTRAP', '<button class="close" aria-label="Close" data-dismiss="alert" type="button">\r\n   <span aria-hidden="true">×</span>\r\n  </button>\r\n    <strong>Holy guacamole!</strong>\r\n     Best check yo self, you''re not looking too good.\r\n</div>\r\n\r\n\r\n<div class="alert alert-danger alert-dismissible fade in" role="alert">\r\n     <button class="close" aria-label="Close" data-dismiss="alert" type="button">\r\n          <span aria-hidden="true">×</span>\r\n     </button>\r\n<h4>Oh snap! You got an error!</h4>\r\n<p>Change this and that and try again. Duis mollis, est non commodo luctus, nisi erat porttitor ligula, eget lacinia odio sem nec elit. Cras mattis consectetur purus sit amet fermentum.</p>\r\n\r\n<p>\r\n   <button class="btn btn-danger" type="button">Take this action</button>\r\n  <button class="btn btn-default" type="button">Or do this</button>\r\n</p>\r\n</div>', '2015-02-09'),
