(383, 'SQL-SERVER Basico', 'C#', '...\r\nusing System.Data.SqlClient;\r\nusing System.Data;\r\n\r\nnamespace ConexionSQL\r\n{\r\n    class Conexion\r\n    {\r\n        // Crear la conexion\r\n        private string cadena = "data source = PC\\\\SQLEXPRESS; initial catalog = PDHM; integrated security = true";\r\n        public SqlConnection con;\r\n        public DataSet ds = new DataSet();\r\n        public SqlDataAdapter da;\r\n        public SqlCommand comando;\r\n        public DataTable dt;\r\n        \r\n        \r\n        // Metodo para la consulta a la base de datos\r\n        public DataTable Consultar1(string tabla) {\r\n            con = new SqlConnection(cadena);\r\n            string sql = "SELECT * FROM " + tabla;\r\n            da = new SqlDataAdapter(sql, con);\r\n            da.Fill(ds, tabla);\r\n            dt = new DataTable();\r\n            dt = ds.Tables[tabla];\r\n            return dt;\r\n        }   \r\n    }\r\n}\r\n\r\n\r\n\r\n\r\nthis.dataGridView1.DataSource = myConexion.Consultar1("alumnos");', '2015-06-30'),
