(385, 'Todo Consola', 'C#', 'Console.WriteLine("This line will appear in the console window");\r\n\r\nnamespace MiAplicacion\r\n{\r\n\r\n    class Pesonas\r\n    {\r\n        public string nombre = "Alex";\r\n        public int edad = 24;\r\n        public int[] notas = { 5, 4, 5 };\r\n    }\r\n    \r\n    class Program\r\n    {\r\n        static void Main(string[] args)\r\n        {\r\n            Pesonas Miper = new Pesonas();\r\n            Console.WriteLine(Miper.nombre);\r\n            Thread.Sleep(2000);\r\n            Console.Write("Entre la edad:");\r\n            int age  = Convert.ToInt32(Console.ReadLine());\r\n            Console.Write("Entre el nombre:");\r\n            string Name = Console.ReadLine();\r\n Console.WriteLine("Hello {0} you H {1} Years Old", Name, age);\r\n            Console.ReadKey();\r\n        }\r\n\r\n    }\r\n}', '2015-06-30'),
