(393, 'POO CONSOLA C# CLASES', 'C#', 'namespace ejercicio01\r\n{\r\n    class Hombre\r\n    {\r\n        private string nombre;\r\n        private float dinero;\r\n        public void Inicio()\r\n        {\r\n            Console.WriteLine("---------BANCO COMOPROGRAMAR.ORG--------");\r\n            Console.WriteLine("-----------------------------");\r\n            Console.WriteLine("INGRESE SU NOMBRE: ");\r\n            nombre = Console.ReadLine();\r\n            Console.WriteLine("INGRESE SU MONTO DINERO; ");\r\n            dinero = float.Parse(Console.ReadLine());\r\n            Console.Clear();\r\n        }\r\n        public void Imprimir()\r\n        {\r\n            Console.WriteLine("---------BANCO COMOPROGRAMAR.ORG--------");\r\n            Console.WriteLine("-----------------------------");\r\n            Console.WriteLine("NOMBRE:    " + nombre);\r\n            if (dinero > 7000)\r\n            {\r\n                Console.WriteLine("USTED NO PUEDE EXCEDER EL MONTO DE S/ 7000");\r\n            }\r\n            else\r\n            {\r\n                Console.WriteLine("INVERSION: " + dinero);\r\n            }\r\n            Console.ReadLine();\r\n        }\r\n        static void Main(string[] args) \r\n        {\r\n            Hombre per = new Hombre();\r\n            per.Inicio();\r\n            per.Imprimir();\r\n\r\n        }\r\n    }', '2015-06-30'),
(394, 'Sentencia IF ELSE', 'C#', 'static void Main(string[] args) {\r\n if (args.Length == 0) {\r\n  Console.WriteLine("No arguments");\r\n } \r\n else {\r\n  Console.WriteLine("One or more arguments");\r\n }\r\n}', '2015-06-30'),
(395, 'Sentencia switch', 'C#', 'static void Main(string[] args) {\r\n int n = args.Length;\r\n switch (n) {\r\n  case 0:\r\n   Console.WriteLine("No arguments");\r\n   break;\r\n  case 1:\r\n   Console.WriteLine("One argument");\r\n   break;\r\n  default:\r\n   Console.WriteLine("{0} arguments", n);\r\n   break;\r\n  }\r\n }\r\n}', '2015-06-30'),
(396, 'Sentencia While', 'C#', 'static void Main(string[] args) {\r\n int i = 0;\r\n while (i < args.Length) {\r\n  Console.WriteLine(args[i]);\r\n  i++; \r\n }\r\n}', '2015-06-30'),
(397, 'Sentencia FOR', 'C#', 'static void Main(string[] args) {\r\n for (int i = 0; i < args.Length; i++) {\r\n  Console.WriteLine(args[i]);\r\n }\r\n}\r\n\r\n// Sentencia interrumpida\r\nfor (int counter = 1; counter <= 1000; counter++) {\r\n    if (counter == 10) \r\n       break;\r\n    Console.WriteLine(counter);\r\n}', '2015-06-30'),
(398, 'Leer Archivo de Texto', 'C#', 'int counter = 0;\r\nstring line;\r\n// Read the file and display it line by line.\r\nSystem.IO.StreamReader file =    new System.IO.StreamReader("c:\\test.txt");\r\nwhile((line = file.ReadLine()) != null)\r\n{\r\n   Console.WriteLine (line);\r\n   counter++;\r\n}\r\n\r\nfile.Close();\r\n// Suspend the screen.\r\nConsole.ReadLine();', '2015-06-30'),
(399, 'Escribir en un archivo de texto', 'C#', '// Compose a string that consists of three lines.\r\nstring lines = "First line.\\r\\nSecond line.\\r\\nThird line.";\r\n// Write the string to a file.\r\nSystem.IO.StreamWriter file = new System.IO.StreamWriter("c:\\\\test.txt");\r\nfile.WriteLine(lines);\r\nfile.Close();', '2015-06-30'),
(400, 'Minimizar en la barra de tarea', 'C#', 'namespace WindowsFormsApplication12\r\n{\r\n    public partial class Form1 : Form\r\n    {\r\n        public Form1()\r\n        {\r\n            InitializeComponent();\r\n        }\r\n\r\n        private void minimizarToolStripMenuItem_Click(object sender, EventArgs e)\r\n        {\r\n            this.Hide();\r\n notifyIcon1.Visible = true;\r\n notifyIcon1.BalloonTipText = "y Texto \\n\\nsugerencias\\n";\r\n notifyIcon1.BalloonTipTitle = "Aquí va el título:";\r\n notifyIcon1.BalloonTipIcon = ToolTipIcon.Info;\r\n notifyIcon1.ShowBalloonTip(5000);\r\n        }\r\n\r\n\r\n        private void mostrarToolStripMenuItem_Click(object sender, EventArgs e)\r\n        {\r\n            this.Visible = !this.Visible;\r\n         }\r\n\r\n        private void salirToolStripMenuItem_Click(object sender, EventArgs e)\r\n        {\r\n            this.Dispose();\r\n        }\r\n\r\n        private void Form1_Load(object sender, EventArgs e)\r\n        {\r\n        \r\n        }\r\n    }\r\n}', '2015-06-30'),
(401, 'Insertar Dato Data Grid', 'C#', 'int rowEscribir = dataGridView1.Rows.Count - 1;\r\n\r\ndataGridView1.Rows.Add(1);\r\ndataGridView1.Rows[rowEscribir].Cells[0].Value = Valor1;\r\ndataGridView1.Rows[rowEscribir].Cells[1].Value = Valor2;\r\ndataGridView1.Rows[rowEscribir].Cells[2].Value = Valor3;\r\ndataGridView1.Rows[rowEscribir].Cells[4].Value = Valor4;', '2015-06-30'),
(402, 'Conectarse a Mysql dede C#', 'C#', 'using MySql.Data.MySqlClient;\r\n    \r\nclass BDConnect\r\n    {\r\n        private string server;\r\n        private string database;\r\n        private string uid;\r\n        private string password;\r\n\r\n        public BDConnect( string server, string database, string uid , string password) {\r\n            this.server = server;\r\n            this.database = database;\r\n            this.uid = uid;\r\n            this.password = password;\r\n            string connectionString;\r\nconnectionString = "SERVER=" + server + ";" + "DATABASE=" + database + ";" + "UID=" + uid + ";" + "PASSWORD=" + password + ";";            \r\n            MySqlConnection connection = new MySqlConnection(connectionString);\r\n            try {\r\n                connection.Open();\r\n                MessageBox.Show("OK");\r\n            }\r\n            catch (MySqlException ex)\r\n            {   switch (ex.Number)\r\n                {\r\n                    case 0:\r\n                        MessageBox.Show("Cannot connect to server.  Contact administrator");\r\n                        break;\r\n                    case 1045:\r\n                        MessageBox.Show("Invalid username/password, please try again");\r\n                        break;\r\n                }              \r\n            }        }\r\n\r\n    }', '2015-06-30'),
(403, 'foreach', 'C#', 'using System;\r\n\r\nclass ForEachTest {\r\n\r\n	public static void Main() {\r\n		int [] array = {11,12,13,14,15};\r\n		string testStr = "abc123";\r\n		for (int i = 0; i < array.Length; i++)\r\n			Console.WriteLine(array[i]);\r\n		foreach (int i in array)\r\n			Console.WriteLine(i);\r\n		foreach (char c in testStr)\r\n			Console.WriteLine(c);\r\n		Console.ReadLine();\r\n	}\r\n}', '2015-06-30'),
(404, 'Array', 'C#', 'using System;\r\npublic class Tester {\r\n	static void Main() {\r\n		String[] myArray = {"Who", "is", "John", "Doe"};\r\n		PrintMyArray(myArray);\r\n		Array.Reverse(myArray);\r\n		PrintMyArray(myArray);\r\n		String[] myOtherArray = {"We", "Hold", "These", "Truths", "To", "Be", "Self", "Evident"};\r\n\r\n		PrintMyArray(myOtherArray);\r\n		Array.Sort(myOtherArray);\r\n		PrintMyArray(myOtherArray);\r\n		Console.ReadLine();\r\n	}\r\n	public static void PrintMyArray(object[] theArray) {\r\n		foreach (object obj in theArray)\r\n			Console.WriteLine("Value: {0}", obj);\r\n		Console.WriteLine();\r\n	}\r\n}', '2015-06-30'),
(405, 'Arreglos', 'C#', 'using System;\r\nclass Test \r\n{\r\n static void Main() {\r\n  int[] a = new int[10];\r\n  for (int i = 0; i < a.Length; i++) {\r\n   a[i] = i * i;\r\n  }\r\n  for (int i = 0; i < a.Length; i++) {\r\n   Console.WriteLine("a[{0}] = {1}", i, a[i]);\r\n  }\r\n }\r\n} \r\n\r\n\r\nint[] notas = { 5, 4, 5 };', '2015-06-30'),
(406, 'Obtener IP de tu maquina', 'C#', 'using System.Net;\r\nusing System.Net.NetworkInformation;\r\n\r\nprivate void button1_Click(object sender, EventArgs e)\r\n        {\r\n            IPHostEntry host;\r\n            string localIP = "";\r\n            host = Dns.GetHostEntry(Dns.GetHostName());\r\n            foreach (IPAddress ip in host.AddressList)\r\n            {\r\n              if (ip.AddressFamily.ToString() == "InterNetwork")\r\n                {\r\n                    localIP = ip.ToString();\r\n                }\r\n            }\r\n            MessageBox.Show("Tú IP Local Es: " + localIP);\r\n        }', '2015-06-30'),
(407, 'Buscar texto dentro de una cadena', 'C#', 'string searchWithinThis = "ABCDEFGHIJKLMNOP";\r\nstring searchForThis = "DEF";\r\nint firstCharacter = searchWithinThis.IndexOf(searchForThis);\r\n\r\nConsole.WriteLine("First occurrence: {0}", firstCharacter)', '2015-06-30'),
(408, 'Solo Números en los Textbox C# - Windows Forms', 'C#', 'private void txtCaracter_KeyPress(object sender, KeyPressEventArgs e)\r\n{\r\nif (!(char.IsNumber(e.KeyChar)) && (e.KeyChar != (char)Keys.Back))\r\n{\r\n      MessageBox.Show("Solo se permiten numeros", "Advertencia", MessageBoxButtons.OK, MessageBoxIcon.Exclamation);\r\n      e.Handled = true;\r\n      return;\r\n}\r\n}', '2015-06-30'),
(409, 'Redondear en C#', 'C#', 'Math.Round(3.44, 1) ''Retorna 3.4.', '2015-06-30'),
(410, 'Formulario', 'ASP .NET', '<form runat="server" class="form-signin">\r\n<asp:TextBox ID="txtUsuario" runat="server" type="text" class="form-control" placeholder="Correo" required autofocus></asp:TextBox>\r\n\r\n<asp:TextBox ID="txtPass" runat="server" type="password" class="form-control" placeholder="Contraseña" required></asp:TextBox>\r\n\r\n<asp:Button ID="btnIngresar" runat="server" class="btn btn-lg btn-primary btn-block" type="submit"  Text="Sign in"  OnClick="btnIngresar_Click"/>\r\n                            \r\n    <label class="checkbox pull-left">\r\n      <input type="checkbox" value="remember-me">\r\n       Remember me\r\n     </label>\r\n  <a href="#" class="pull-right need-help">Need help? </a><span class="clearfix"></span>\r\n</form>', '2015-06-30'),
(411, 'C# Excepciones', 'C#', 'namespace Exepciones\r\n{\r\n    class Program\r\n    {\r\n        static void Main(string[] args)\r\n        {\r\n            try\r\n            {\r\n                Console.WriteLine("Entre el numero");\r\n                int numero = int.Parse(Console.ReadLine());\r\n                Console.WriteLine("Entre el valor");\r\n                int valor = int.Parse(Console.ReadLine());\r\n                double result = RaizEnesima(numero, valor);\r\n                Console.WriteLine("El resultado es:"+result);\r\n            }\r\n \r\n            catch (FormatException) {Console.WriteLine("Ha ocurrido un error en la entrada de datos");\r\n            }\r\n\r\n            catch (OverflowException)\r\n            {\r\n                Console.WriteLine("Valor demasiado grande para almacenar en un int");\r\n\r\n            }\r\n            catch (Exception ex)\r\n            {\r\n                Console.WriteLine(ex.Message);\r\n            }\r\n            Console.ReadKey();\r\n        }\r\n\r\n        public static double RaizEnesima(int numero, int n)\r\n        {\r\n            if (n == 0)\r\n            throw new Exception("No es posible dividir por 0"); \r\n            return Math.Pow((double)numero, 1.0 / (double)n);\r\n        }\r\n    }\r\n}', '2015-06-30'),
(412, 'C# y Acces', 'C#', '** Ejecuta un INSERT */\r\n         static void insertar(String nombre, String apellido) \r\n         {\r\n         OleDbCommand cmd = new OleDbCommand();\r\n         //Representa una instruccion SQL\r\n         cmd.Connection = con;\r\n         //asigna la conexion a la base de datos\r\n       Console.Write("Insertando registros...");\r\n  String query = "INSERT INTO Alumno(nombre,apellido) VALUES(''"+nombre+"'',''"+apellido+"'')";            \r\n       cmd.CommandText = @query;//asigna la instruccion SQL\r\n       cmd.ExecuteNonQuery();//ejecuta una instruccion SQL\r\n       Console.WriteLine("ok");\r\n        } \r\n  /** Ejecuta un UPDATE \r\n  * Id Llave primaria\r\n  * Set Ej. "Nombre=''nuevo valor''"\r\n  */\r\n         static void actualizar(int Id, String Set) \r\n         {\r\n             OleDbCommand cmd = new OleDbCommand();\r\n             cmd.Connection = con;\r\n             Console.Write("Actualizando...");\r\n   String query = "UPDATE Alumno SET "+Set+" WHERE Id="+Id;\r\n             cmd.CommandText = @query;            \r\n             cmd.ExecuteNonQuery();\r\n             Console.WriteLine("ok");\r\n         }\r\n \r\n        /** Elimina un registro dado su ID */\r\n         static void eliminar(int Id) \r\n         {\r\n             OleDbCommand cmd = new OleDbCommand();\r\n             cmd.Connection = con;\r\n            Console.Write("Eliminando...");\r\n             String query = "DELETE FROM Alumno WHERE Id="+Id;            \r\n             cmd.CommandText = @query;\r\n             cmd.ExecuteNonQuery();\r\n            Console.WriteLine("ok");\r\n         }\r\n \r\n    }\r\n }', '2015-06-30'),
(413, 'C# Acces Agenda - Ejemplo de Acces', 'C#', 'En la forma con el DataGrid\r\n//////////////////////////////////////////////////////////////\r\nDatos MyDatos = new Datos();\r\nMyDataGrid.DataSource = MyDatos.Consultar(temp2);\r\n\r\n//////////////////////////////////////////////////////////////\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\nusing System.Threading.Tasks;\r\nusing System.Data.OleDb;\r\nusing System.Collections;\r\nusing System.Data.SqlClient;\r\nusing System.Data;\r\n\r\nnamespace MyAgendaNet\r\n{\r\n    class Datos\r\n    {\r\n       private  static OleDbConnection con;\r\n       private static String ds = "MyAgendaNetDB.mdb";\r\n       private  static String stringConexion = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=" + ds;\r\n\r\n\r\n\r\n  // Metodo para conectar a la Base de datos\r\n        public string Conectar() {\r\n            string cadena;\r\n            try\r\n            {\r\n                con = new OleDbConnection(@stringConexion);\r\n                con.Open();//se abre una conexion\r\n                cadena = "Conectado";\r\n            }\r\n            catch (System.Exception ex)\r\n            {\r\n                cadena = "ERROR:" + ex.Message;\r\n            }\r\n            return cadena;\r\n        }\r\n\r\n\r\n    // Metodo para buscar las categorias\r\n        public ArrayList DameCategoria()\r\n        {\r\n            string sql = @"SELECT nombre FROM Categoria ORDER BY nombre";\r\n            OleDbCommand cmd = new OleDbCommand();\r\n            cmd.Connection = con;\r\n            cmd.CommandText = sql;\r\n            OleDbDataReader reader = cmd.ExecuteReader();\r\n            ArrayList valuesList = new ArrayList();\r\n            while (reader.Read())\r\n            {\r\n                if (DameTotaTrucosbyName(reader[0].ToString()) != 0) \r\n                    valuesList.Add(reader[0].ToString());\r\n            }\r\n            return valuesList;\r\n        }\r\n\r\n\r\n    \r\n     // Buscamos en la tabla categoria el id para un nombre\r\n        public string DameIdbyNombre(string Nombre) {\r\n            string cadena = "";\r\n            string sql = @"SELECT id FROM Categoria WHERE nombre = ''" + Nombre + "''";\r\n            OleDbCommand cmd = new OleDbCommand();\r\n            cmd.Connection = con;\r\n            cmd.CommandText = sql;\r\n            OleDbDataReader reader = cmd.ExecuteReader();\r\n            if (reader.HasRows)\r\n              {\r\n                  while (reader.Read())\r\n                  {\r\n                      cadena = reader[0].ToString();\r\n                  }\r\n              }\r\n         \r\n            return cadena;            \r\n        }\r\n\r\n\r\n\r\n     //Buscamos el trucos teniendo el nombre\r\n        public string DameCodeByName(string Name)\r\n        {\r\n            string cadena = "";\r\n            string sql = @"SELECT texto FROM Codigos WHERE nombre = ''" + Name + "''";\r\n            OleDbCommand cmd = new OleDbCommand();\r\n            cmd.Connection = con;\r\n            cmd.CommandText = sql;\r\n            OleDbDataReader reader = cmd.ExecuteReader();\r\n            if (reader.HasRows)\r\n            {\r\n                while (reader.Read())\r\n                {\r\n                    cadena = reader[0].ToString();\r\n                }\r\n            }\r\n\r\n            return cadena;  \r\n        }\r\n\r\n\r\n      // Contamos la cantidad total de trucos\r\n        public int DameTotalTrucos() {\r\n            string sql = @"SELECT * FROM Codigos";\r\n            OleDbCommand cmd = new OleDbCommand();\r\n            cmd.Connection = con;\r\n            cmd.CommandText = sql;\r\n            int i = 0;\r\n            OleDbDataReader reader = cmd.ExecuteReader();\r\n            if (reader.HasRows)\r\n            {\r\n                while (reader.Read())\r\n                {\r\n                    i++;\r\n                }\r\n            }\r\n            return i;        \r\n        }\r\n\r\n\r\n        // Contamos la cantidad total de trucos\r\n        public int DameTotaTrucosbyName(string Name)\r\n        {\r\n            string idcate =  Convert.ToString(DameIdbyNombre(Name));\r\n            string sql = @"SELECT * FROM Codigos WHERE idcategoria =  " + idcate;\r\n            OleDbCommand cmd = new OleDbCommand();\r\n            cmd.Connection = con;\r\n            cmd.CommandText = sql;\r\n            int i = 0;\r\n            OleDbDataReader reader = cmd.ExecuteReader();\r\n            if (reader.HasRows)\r\n            {\r\n                while (reader.Read())\r\n                {\r\n                    i++;\r\n                }\r\n            }\r\n            return i;\r\n        }\r\n        // Consulta de seleccion para llenar el grid\r\n        public DataTable Consultar(string idCategoria) {\r\n            string sql = @"SELECT nombre,texto FROM Codigos WHERE idcategoria=" + idCategoria;\r\n            OleDbCommand cmd = new OleDbCommand();\r\n            cmd.Connection = con;\r\n            cmd.CommandText = sql;\r\n            OleDbDataReader reader = cmd.ExecuteReader();            \r\n            DataTable dt = new DataTable();\r\n            //dt = reader.GetSchemaTable();\r\n            dt.Load(reader);\r\n            return dt;           \r\n        }\r\n    }\r\n}', '2015-06-30'),
(414, 'C# Validacion .:.  errorProvider1', 'C#', 'private bool is_validate()\r\n        {\r\n            bool no_error = true;\r\n\r\n            if (textBox1.Text == string.Empty)\r\n            {\r\n                erroricono.SetError(textBox1, "Ingrese su Nombre");\r\n                no_error = false;\r\n            }\r\n            else {\r\n                try\r\n                {\r\n                    int i = Convert.ToInt32(textBox2.Text);\r\n                }\r\n                catch {\r\n        erroricono.Clear();\r\n        erroricono.SetError(textBox2, "Ingrese la Edad");\r\n                    return false;\r\n                }\r\n\r\n                try\r\n                {\r\n                    double f = Convert.ToDouble(textBox3.Text);\r\n                }\r\n                catch\r\n                {\r\n                    erroricono.Clear();\r\n                    erroricono.SetError(textBox3, "Ingrese un Salario Valido");\r\n                    return false;\r\n                }\r\n            }\r\n            return no_error;\r\n        }\r\n        private void button1_Click(object sender, EventArgs e)\r\n        {\r\n            if (is_validate()) {\r\n                erroricono.Clear();\r\n                MessageBox.Show("Datos agregados correctamente","Validaciones", MessageBoxButtons.OK, MessageBoxIcon.Information);\r\n            }\r\n        }', '2015-06-30'),
(415, 'Balon de informacion', 'C#', 'namespace WindowsFormsApplication1\r\n{\r\n    public partial class Form1 : Form\r\n    {\r\n        public Form1()\r\n        {\r\n            InitializeComponent();\r\n this.toolTip1.SetToolTip(this.textBox1,"entre Nombre");\r\n this.toolTip1.SetToolTip(this.textBox2, "entreedad");\r\n        }\r\n\r\n private void Form1_Load(object sender, EventArgs e)\r\n        {\r\n\r\n        }\r\n    }\r\n}', '2015-06-30'),
(416, ' C# Acces', 'C#', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\nusing System.Data.OleDb;\r\n \r\nnamespace ConAccessOperations\r\n{\r\n    class Program\r\n    {\r\n  static OleDbConnection con;//Representa una conexión abierta a un origen de datos\r\n  static String ds = "E:/dbalumno.mdb"; //direccion de la base de datos Access\r\n        static String stringConexion = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=" + ds;\r\n \r\n         static void Main(string[] args)\r\n         {\r\n             conectar();             \r\n            //Se imprimir los registros\r\n            imprimirRegistros();\r\n             //se añaden dos nuevos registros\r\n            insertar("Jorge","Nitales");\r\n            insertar("Elsa", "Pallo");\r\n            imprimirRegistros();\r\n             //Se actualiza el registro con ID=4\r\n             actualizar(4,"apellido=''Gordito''");\r\n             imprimirRegistros();\r\n             //Se elimina \r\n             eliminar(2);\r\n             imprimirRegistros();\r\n\r\n            desconectar();\r\n  Console.WriteLine("Presione cualquier tecla para terminar");\r\n             Console.ReadKey();\r\n         }\r\n\r\n\r\n//////////////////////////////////////////////////////////////\r\n\r\n/** Abre una conexion a la base de datos */\r\n         static void conectar() \r\n         {\r\n             try\r\n             {\r\n                 con = new OleDbConnection(@stringConexion);\r\n                 con.Open();//se abre una conexion\r\n         Console.WriteLine("Conectado a la base de datos.");                \r\n             }\r\n            catch (System.Exception ex)\r\n             {\r\n                 Console.WriteLine("ERROR:" + ex.Message);\r\n             }\r\n        } \r\n/** Cierra la conexion a la base de datos */\r\n         static void desconectar() \r\n         {\r\n             con.Close();\r\n             Console.WriteLine("La conexion ha terminado.");\r\n         }\r\n\r\n/** Extrae registros y los muestra en pantalla */\r\n         static void imprimirRegistros() \r\n         {\r\n             OleDbCommand cmd = new OleDbCommand();\r\n             cmd.Connection = con;                        \r\n  cmd.CommandText = @"SELECT Id,nombre,apellido FROM Alumno";\r\n\r\n//Ejecuta la instruccion SQL y retorna el resultado\r\n            OleDbDataReader reader = cmd.ExecuteReader();\r\n        Console.WriteLine("------------------");\r\n\r\n//va leyendo los registros y mostrando en pantalla\r\n             while (reader.Read())\r\n             {\r\n            Console.WriteLine("{0} | {1} | {2}", reader.GetValue(0).ToString(),                                               reader.GetValue(1).ToString(),                                                      reader.GetValue(2).ToString());\r\n               }\r\n       Console.WriteLine("-----------------------------------");\r\n             reader.Close();\r\n         }', '2015-06-30'),
(417, 'Ini C#', 'C#', 'namespace IniProject\r\n{    \r\n    public partial class Form1 : Form\r\n    {      \r\n        public Form1()\r\n        {\r\n            InitializeComponent();\r\n           \r\n        }        \r\n\r\n        IniFile MyIni = new IniFile("Settings.ini");\r\n        private void Form1_Load(object sender, EventArgs e)\r\n        {\r\n            this.textBox1.Text = MyIni.Read("DefaultVolume");\r\n            this.textBox2.Text = MyIni.Read("HomePage");\r\n        }\r\n\r\n        private void button1_Click(object sender, EventArgs e)\r\n        {\r\n            MyIni.Write("DefaultVolume", this.textBox1.Text);\r\n            MyIni.Write("HomePage", this.textBox2.Text);\r\n        }\r\n    }\r\n}\r\n    , using System.IO;\r\nusing System.Reflection;\r\nusing System.Runtime.InteropServices;\r\n\r\nnamespace IniProject\r\n{\r\n    class IniFile   // revision 10\r\n    {\r\n        string Path;\r\n        string EXE = Assembly.GetExecutingAssembly().GetName().Name;\r\n\r\n        [DllImport("kernel32")]\r\n        static extern long WritePrivateProfileString(string Section, string Key, string Value, string FilePath);\r\n\r\n        [DllImport("kernel32")]\r\n        static extern int GetPrivateProfileString(string Section, string Key, string Default, StringBuilder RetVal, int Size, string FilePath);\r\n\r\n        public IniFile(string IniPath = null)\r\n        {\r\n   Path = new FileInfo(IniPath ?? EXE + ".ini").FullName.ToString();\r\n        }\r\n        public string Read(string Key, string Section = null)\r\n        {\r\n            var RetVal = new StringBuilder(255);\r\n            GetPrivateProfileString(Section ?? EXE, Key, "", RetVal, 255, Path);\r\n            return RetVal.ToString();\r\n        }\r\n   public void Write(string Key, string Value, string Section = null)\r\n        {\r\n            WritePrivateProfileString(Section ?? EXE, Key, Value, Path);\r\n        }\r\n        public void DeleteKey(string Key, string Section = null)\r\n        {\r\n            Write(Key, null, Section ?? EXE);\r\n        }\r\n        public void DeleteSection(string Section = null)\r\n        {\r\n            Write(null, null, Section ?? EXE);\r\n        }\r\n\r\n        public bool KeyExists(string Key, string Section = null)\r\n        {\r\n            return Read(Key, Section).Length > 0;\r\n        }\r\n    }\r\n}', '2015-06-30'),
(418, 'C# y Acces Basico', 'C#', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\nusing System.Data.OleDb;//Para base de datos Access\r\n\r\nnamespace AccessCon\r\n{\r\n    class Program\r\n     {\r\n         static void Main(string[] args)\r\n        {\r\n        OleDbConnection con;//Representa una conexión abierta \r\n        String ds = "E:/dbproblem.mdb"; //direccion de la base de datos Access\r\n\r\nString stringConexion = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source="+ds;\r\n\r\n            try {\r\n                con = new OleDbConnection(@stringConexion);\r\n                con.Open();//se abre una conexion\r\n      Console.WriteLine("Conectado a la base de datos.");\r\n                con.Close();//se cierra la conexion\r\n                Console.WriteLine("La conexion ha terminado.");\r\n             }\r\n             catch (System.Exception ex)\r\n            {\r\n                 Console.WriteLine("ERROR:" + ex.Message);\r\n            }\r\n Console.WriteLine("Presione cualquier tecla para terminar");\r\n             Console.ReadKey();\r\n         }\r\n    }\r\n }', '2015-06-30'),
(419, 'Dialogo Yes No', 'C#', '\r\n\r\nDialogResult result = MessageBox.Show("Do you want to save changes?", "Confirmation", MessageBoxButtons.YesNoCancel);\r\nif(result == DialogResult.Yes)\r\n{ \r\n    //...\r\n}\r\nelse if (result == DialogResult.No)\r\n{ \r\n    //...\r\n}\r\nelse\r\n{\r\n    //...\r\n}', '2015-06-30'),
(420, 'Conectar PHP - SQLSERVER', 'SQLSERVER', '<?php\r\n$serverName = "SERVER/USUARIO";\r\n\r\n$connectionOptions = array("Database"=>"base","UID"=>"usuario","PWD"=>"pass","ReturnDatesAsStrings"=>"true");\r\n$link = sqlsrv_connect( $serverName, $connectionOptions);\r\n\r\nif( $link === false )\r\ndie( printr( sqlsrverrors() ) );\r\n?>\r\n\r\nEsto lo pongo en un archivo a parte como conexión.php ya en las páginas que lo ocupo solo lo traigo como include, ahora veamos una query sencillo\r\n\r\n$sql = "select * from mitabla";\r\n$conexion = sqlsrvquery($link,$sql);\r\nwhile($row = sqlsrvfetcharray($conexion)){\r\n\r\necho $row["campo_tabla"]."\r\n";\r\n\r\n}\r\nsqlsrv_close($link);\r\n\r\n?>', '2015-06-30'),
(421, 'Procedimientos Almacenados SQLSERVER', 'SQLSERVER', '--Procedimiento mostrar\r\ncreate proc spmostrarcategoria\r\nas\r\nselect top 200 * from categoria\r\norder by idcategoria desc\r\ngo\r\n\r\n--Procedimiento buscar Nombre\r\ncreate proc spbuscarcategoria\r\n@textobuscar varchar(50)\r\nas\r\nselect * from categoria\r\nwhere nombre like @textobuscar + ''%''\r\ngo\r\n\r\n--Procedimiento Insertar\r\ncreate proc spinsertarcategoria\r\n@idcategoria int output,\r\n@nombre varchar(50),\r\n@descripcion varchar(256)\r\nas\r\ninsert into categoria (nombre,descripcion)\r\nvalues (@nombre,@descripcion)\r\ngo\r\n\r\n--Procedimiento Editar\r\ncreate proc speditarcategoria\r\n@idcategoria int,\r\n@nombre varchar(50),\r\n@descripcion varchar(256)\r\nas\r\nupdate categoria set nombre = @nombre,\r\ndescripcion = @descripcion\r\nwhere idcategoria = @idcategoria\r\ngo\r\n\r\n--Procedimiento eliminar\r\ncreate proc speliminarcategoria\r\n@idcategoria int\r\nas\r\ndelete from categoria\r\nwhere idcategoria=@idcategoria\r\ngo', '2015-06-30'),
(422, 'Tabla con Busqueda en Angular', 'ANGULAR JS', '<!DOCTYPE html>\r\n<html lang="es">\r\n<head>\r\n	<meta charset="UTF-8">\r\n	<title>Bootstrap</title>\r\n	<meta name="viewport" content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">\r\n	<link rel="shortcut icon" href="favicon.ico" type="image/x-icon"/>\r\n  <link rel="stylesheet" href="assets/css/bootstrap.min.css">\r\n  <link rel="stylesheet" href="assets/css/font-awesome.css">\r\n  <link rel="stylesheet" href="style.css">\r\n  \r\n<!-- HTML5 Shim and Respond.js IE8 support of HTML5 elements and media queries -->\r\n  <!--[if lt IE 9]>\r\n      <script src="https://oss.maxcdn.com/libs/html5shiv/3.7.0/html5shiv.js"></script>\r\n      <script src="https://oss.maxcdn.com/libs/respond.js/1.4.2/respond.min.js"></script>\r\n  <![endif]-->\r\n\r\n</head>\r\n<body ng-app>\r\n\r\n<header>\r\n   <a href="http://www.marlonfalcon.cl/">\r\n     <img alt="marlon" src="assets/img/right_orange.png" style="position: fixed; top: 0; right: 0; border: 0;z-index:9999;"  id="marlon" >\r\n   </a>\r\n</header>\r\n\r\n<!-- Angular JS -->\r\n<script>\r\n   function ControladorFiltros($scope) {\r\n       $scope.empleados = \r\n        [\r\n           { nombre: ''Ana'', apellido: ''Guzman'', telefono: ''123445'', salario: ''5234'' , primerDia: new Date()  },\r\n           { nombre: ''Yoel'', apellido: ''Auzman'', telefono: ''123445'', salario: ''5234'', primerDia: new Date()    },\r\n           { nombre: ''Martha'', apellido: ''Gaazman'', telefono: ''123445'', salario: ''5234'', primerDia: new Date() },\r\n           { nombre: ''Ana'', apellido: ''Guzman'', telefono: ''123445'', salario: ''5234'' , primerDia: new Date()  },\r\n           { nombre: ''Yoel'', apellido: ''Auzman'', telefono: ''123445'', salario: ''5234'', primerDia: new Date()    },\r\n           { nombre: ''Martha'', apellido: ''Gaazman'', telefono: ''123445'', salario: ''5234'', primerDia: new Date() }\r\n        ];\r\n\r\n        $scope.ordenarPor = function (orden) {\r\n           $scope.ordenSelecccionado = orden;\r\n        }\r\n   }\r\n</script>\r\n\r\n\r\n<div class="container" ng-controller="ControladorFiltros">\r\n    <h1> Filtros Nativos de Angular JS </h1>\r\n    <hr>\r\n      <div class="panel panel-primary">\r\n           <div class="panel-heading">\r\n               <div class="input-group">\r\n                   <span class="input-group-addon"><i class="glyphicon glyphicon-search"></i></span>\r\n                   <input type="text" class="form-control" placeholder="Buscare Empleado" ng-model="buscar.nombre">\r\n               </div>\r\n          </div>\r\n       <div class="panel-body">\r\n          <table class="table table-striped">\r\n              <thead>\r\n                 <tr>\r\n                    <th><a href="" ng-click="ordenarPor(''nombre'')">Nombres</a>\r\n                    <span class="caret" style="cursor:pointer" ng-click="ordenarPor(''-nombre'')" ></span></th>\r\n                    <th><a href="" ng-click="ordenarPor(''apellidos'')">Apellidos</a></th>\r\n                    <th>Telefono</th>\r\n                    <th>Salario</th>\r\n                    <th>Cumpleaño</th>\r\n                 </tr>\r\n              </thead>\r\n              <tbody>\r\n                  <tr ng-repeat="empleado in empleados | orderBy:ordenSelecccionado | filter:buscar">\r\n                     <td>{{empleado.nombre  }}</td>\r\n                     <td>{{empleado.apellido | uppercase}}</td>\r\n                     <td>{{empleado.telefono | number:2 }}</td>\r\n                     <td>{{empleado.salario | currency }}</td>\r\n                     <td>{{empleado.primerDia | date:''fullDate'' }}</td>\r\n                  </tr>\r\n              </tbody>\r\n          </table>\r\n       </div>    \r\n      </div>  <!-- Panel Primario -->  \r\n    \r\n</div>\r\n<!-- Angular JS -->\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<!-- Si usas redes sociales -->\r\n  <div id="social-sidebar">\r\n     <ul>\r\n       <li><a class="entypo-twitter" target="_blank" href="https://twitter.com/marlonfalcon4"> <i class="fa fa fa-twitter  fa-stack-1x"></i>  </a></li>\r\n       <li><a class="entypo-facebook" target="_blank" href="https://www.facebook.com/falconsoftsa"><i class="fa fa fa-facebook fa-stack-1x"></i> </a></li>\r\n       <li><a class="entypo-youtube" target="_blank" href="https://www.youtube.com/channel/UCM93kgnjXu393jgKjjSkUjQ"><i class="fa fa fa-youtube fa-stack-1x"></i> </a></li>\r\n     </ul>\r\n  </div>\r\n<!-- Si usas redes sociales -->\r\n\r\n\r\n\r\n\r\n<!-- Si usas tu top -->\r\n<div id="toTop" class="back-to-top" style="display: none;">\r\n      <i class="fa fa fa-chevron-up fa-stack-1x"></i>\r\n</div>\r\n<script src="assets/js/jquery-1.11.1.min.js"></script>\r\n<script>            \r\n      $(document).ready(function(){\r\n        var offset = 220;\r\n        var duration = 500;\r\n        $(window).scroll(function() {\r\n          if ($(this).scrollTop() > offset) {\r\n            $(''.back-to-top'').fadeIn(duration);\r\n          } else {\r\n            $(''.back-to-top'').fadeOut(duration);\r\n          }\r\n        });\r\n        \r\n        $(''.back-to-top'').click(function(event) {\r\n          event.preventDefault();\r\n          $(''html, body'').animate({scrollTop: 0}, duration);\r\n          return false;\r\n        })\r\n      });\r\n</script>\r\n<!-- Si usas tu top -->\r\n\r\n  <script src="assets/js/bootstrap.min.js"></script> \r\n  \r\n\r\n<!-- Si usas la libreria fancyBox -->\r\n  <script src="assets/lib/fancyBox/lib/jquery.mousewheel-3.0.6.pack.js"></script>\r\n  <!-- Add fancyBox main JS and CSS files -->\r\n  <script type="text/javascript" src="assets/lib/fancyBox/source/jquery.fancybox.js?v=2.1.5"></script>\r\n  <link rel="stylesheet" type="text/css" href="assets/lib/fancyBox/source/jquery.fancybox.css?v=2.1.5" media="screen" />\r\n\r\n  <!-- Add Button helper (this is optional) -->\r\n  <link rel="stylesheet" type="text/css" href="assets/lib/fancyBox/source/helpers/jquery.fancybox-buttons.css?v=1.0.5" />\r\n  <script type="text/javascript" src="assets/lib/fancyBox/source/helpers/jquery.fancybox-buttons.js?v=1.0.5"></script>\r\n\r\n  <!-- Add Thumbnail helper (this is optional) -->\r\n  <link rel="stylesheet" type="text/css" href="assets/lib/fancyBox/source/helpers/jquery.fancybox-thumbs.css?v=1.0.7" />\r\n  <script type="text/javascript" src="assets/lib/fancyBox/source/helpers/jquery.fancybox-thumbs.js?v=1.0.7"></script>\r\n\r\n  <!-- Add Media helper (this is optional) -->\r\n  <script type="text/javascript" src="assets/lib/fancyBox/source/helpers/jquery.fancybox-media.js?v=1.0.6"></script>\r\n  <script type="text/javascript" src="assets/lib/fancyBox/source/helpers/jquery.fancybox-media.js?v=1.0.6"></script>\r\n \r\n  <script src="assets/lib/fancyBox/lib/function.js"></script>\r\n    <style type="text/css">\r\n    .fancybox-custom .fancybox-skin {\r\n      box-shadow: 0 0 50px #222;\r\n    }\r\n  </style>\r\n<!-- Si usas la libreria fancyBox -->\r\n\r\n\r\n\r\n<!-- progresbar -->\r\n <script src="assets/lib/nprogress/nprogress.js"></script>\r\n <script src="assets/js/angular.min.js"></script>\r\n \r\n <link rel="stylesheet" href="assets/lib/nprogress/nprogress.css">\r\n <script>\r\n    $(''body'').show();\r\n    $(''.version'').text(NProgress.version);\r\n    NProgress.start();\r\n    setTimeout(function() { NProgress.done(); $(''.fade'').removeClass(''out''); }, 1000);\r\n\r\n    $("#b-0").click(function() { NProgress.start(); });\r\n    $("#b-40").click(function() { NProgress.set(0.4); });\r\n    $("#b-inc").click(function() { NProgress.inc(); });\r\n    $("#b-100").click(function() { NProgress.done(); });\r\n  </script>\r\n<!--  progresbar -->\r\n\r\n</body>\r\n\r\n</html>', '2015-06-30'),
(423, 'Formulario Validacion ', 'ANGULAR JS', '<!DOCTYPE html>\r\n<html lang="es">\r\n<head>\r\n  <meta charset ="UTF8">\r\n  <title>Ejemplo Boostrap</title>\r\n  <meta name="viewport" content="width = device-width">\r\n     <!-- HTML5 Shim and Respond.js IE8 support of HTML5 elements and media queries -->\r\n  <!--[if lt IE 9]>\r\n      <script src="https://oss.maxcdn.com/libs/html5shiv/3.7.0/html5shiv.js"></script>\r\n      <script src="https://oss.maxcdn.com/libs/respond.js/1.4.2/respond.min.js"></script>\r\n      <![endif]-->\r\n  <link href="http://getbootstrap.com/dist/css/bootstrap.min.css" rel="stylesheet">\r\n  <script src="http://ajax.googleapis.com/ajax/libs/angularjs/1.2.26/angular.min.js"></script>\r\n    </head>\r\n    <body>\r\n      <div class="container">\r\n       <div class="row">\r\n         <div class="col-lg-6 col-lg-offset-3 ">\r\n          <br><br><br><br><br><br>      \r\n          <form class="form-signin" method="POST" action="assets/include/function.php" role="form" name="forma" ng-app >            \r\n            <div class="form-group ">\r\n              <label for="exampleInputEmail1">Texto</label>\r\n              <input class="form-control inputfill" type="text"  placeholder="Nombre" name="fname" ng-model="usuario.nombre" required>\r\n            </div>            \r\n            <div class="form-group ">\r\n              <label for="exampleInputEmail1">Email address</label>\r\n              <input class="form-control inputfill" type="email" placeholder="Email" name="femail" ng-model="usuario.email" required>\r\n              <div ng-show="forma.femail.$dirty && forma.femail.$invalid">\r\n                 <p class="help-block text-danger" ng-show="forma.femail.$error.required">Campo obligatorio</p>\r\n                 <p class="help-block text-danger" ng-show="forma.femail.$error.email">Email Invalido</p>\r\n              </div>\r\n            </div>              \r\n              <div class="checkbox">\r\n                <label>\r\n                  <input type="checkbox" name="usuariocopia" ng-model="enviar.copia" required> Enviar copia\r\n                </label>\r\n              </div>            \r\n            <input class="btn btn-default" type="submit" value="Insertar" ng-disabled="forma.$invalid">            \r\n          </form>\r\n        </div>\r\n      </div>  \r\n    </div>\r\n  </body>\r\n  </html>', '2015-06-30'),
(424, 'Comando Symfony', 'SYMFONY', '$ php app/console generate:bundle\r\n$ php app/console assets:install --symlink\r\n$ php app/console doctrine:database:create\r\n$ php app/console doctrine:generate:entity\r\n$ php app/console doctrine:schema:create\r\n$ php app/console generate:doctrine:crud\r\n$ php app/console doctrine:schema:update\r\nSymfony2\\> composer update\r\nSymfony2\\> php app/console server:run\r\nhttp://localhost:8000/\r\n\r\nLimpiar Cache\r\nSymfony2> php vendor/sensio/distribution-bundle/Sensio/Bundle/DistributionBundle/Resources/bin/build_bootstrap.php\r\nSymfony2> php app/console cache:clear --env=dev --no-warmup\r\nSymfony2> php app/console cache:clear --env=prod\r\nSymfony2> php app/console assets:install', '2015-06-30'),
(425, 'Comandos COMPOSER', 'COMPOSER', 'Instalar composer\r\ncurl -s http://getcomposer.org/installer | php\r\n\r\nCrear un archivo JSON (composer.json)\r\n{\r\n    "require": {\r\n      "raveren/kint": "dev-master"\r\n    }\r\n}\r\n\r\nEjecutas el comando \r\n> composer install\r\n> composer update\r\n\r\n\r\nInstalando directo el bundle desde composer\r\n$ composer require jmikola/auto-login-bundle=~1.0\r\n\r\nDefiniendo dependencia\r\n{\r\n    "require": {\r\n        "vendor/package": "1.3.2",\r\n        "vendor/package2": "1.*",\r\n        "vendor/package3": ">=2.0.3"\r\n      }\r\n}', '2015-06-30'),
(426, 'Introduccion Symfony', 'SYMFONY', 'Symfony es un completo framework diseñado para optimizar, gracias a sus características, el desarrollo de las aplicaciones web. Para empezar, separa la lógica de negocio, la lógica de servidor y la presentación de la aplicación web. Proporciona varias herramientas y clases encaminadas a reducir el tiempo de desarrollo de una aplicación web compleja.\r\n\r\nhttp://localhost/MySymfony/Symfony243/web/app_dev.php/\r\n\r\nSymfony (Mapeo de Objetos a Bases de datos (ORM)) En symfony los las base de datos son orientadas a objetos. Por este motivo, para acceder a la base de datos como si fuera orientada a objetos, es necesario una interfaz que traduzca la lógica de los objetos a la lógica relacional. Esta interfaz se denomina "mapeo de objetos a bases de datos" (ORM, de sus siglas en inglés "object-relational mapping"). De esta forma, es muy sencillo cambiar a otro sistema de bases de datos completamente diferente en mitad del desarrollo de un proyecto. Para obtener el precio total de los productos del carrito y así realizar el pago, se puede añadir un método llamado getTotal() de la siguiente forma:\r\npublic function getTotal()\r\n{\r\n  $total = 0;\r\n  foreach ($this->getProductos() as $producto)\r\n  {\r\n    $total += $producto->getPrecio() * $item->getCantidad();\r\n  }\r\n  return $total;\r\n}\r\nSymfony es la herramienta ideal para el RAD. De hecho, el framework ha sido desarrollado por una empresa que aplica el RAD a sus propios proyectos. Por este motivo, aprender a utilizar Symfony no es como aprender un nuevo lenguaje de programación, sino que consite en aprender a tomar las decisiones correctas para desarrollar las aplicaciones de forma más efectiva.', '2015-06-30'),
(427, 'Plantilla Symfony', 'SYMFONY', 'ymfony Sistema de plantilla (Twing) Sistema de plantillas.\r\n{{ ... }}: Imprime una variable o el resultado de una expresión;\r\n\r\n{ % ... %}: Controla la lógica de la plantilla; se utiliza para ejecutar bucles for y declaraciones if, por\r\nejemplo.\r\n\r\nTruco: Puedes incluir comentarios dentro de las plantillas con el delimitador {# ... #}\r\n\r\n<!DOCTYPE html>\r\n<html>\r\n  <head>\r\n   <title>{{ page_title }}</title>\r\n    </head>\r\n  <body>\r\n    <h1>{{ page_title }}</h1>\r\n    <ul id="navigation">\r\n      {% for item in navigation %}\r\n<li><a href="{{ item.href }}">{{ item.caption }}</a></li>  \r\n      {% endfor %}\r\n   </ul>\r\n  </body>\r\n</html>\r\n\r\n\r\nLa función path sabe cómo generar URL basándose en la conﬁguración de enrutado. De esta manera,\r\ntodas tus URL se pueden actualizar fácilmente con sólo cambiar la conﬁguración:\r\n\r\n<a href="{{ path(’_demo_hello’, { ’name’: ’Thomas’ }) }}">Greet Thomas!</a> ', '2015-06-30'),
(428, 'Como publicar un proyecto de symfony en un dominio', 'SYMFONY', '1- Tienes que ir al fichero: C:\\xampp\\htdocs\\MySymfony\\Symfony_h1\\composer.json\r\n\r\n"symfony-web-dir": "web",\r\n\r\ny cambiarla por\r\n\r\n"symfony-web-dir": "public_html",\r\n\r\n\r\nhome\r\n    public_html\r\n        (aca adentro va app.php, los assets, etc., todo lo que tenias dentro de /web)\r\n    public_ftp\r\n    app\r\n    vendor\r\n    src\r\n    components\r\n\r\n\r\n', '2015-06-30'),
(429, 'Como poner una pagina en el home', 'SYMFONY', '2- Tienes que ir al fichero: routing.yml:\r\n    pattern:  /\r\n    defaults: { _controller:             marlonejemploBundle:Default:home }', '2015-06-30'),
(430, 'Poner un enlace', 'SYMFONY', '3- Tienes que ir a la platilla twing.\r\n<a href="{{ path(''alumnos'') }}">Alumnos</a>', '2015-06-30'),
(431, 'Plantilla Symfony Twig', 'SYMFONY', 'Symfony Muy a menudo, las plantillas en un proyecto comparten elementos comunes, como los bien conocidos encabezadosy pies de página. En Symfony2, nos gusta pensar en este problema de forma diferente: una plantilla se puede decorar\r\ncon otra.\r\n\r\nLa plantilla hello.html.twig hereda de base.html.twig, gracias a la etiqueta extends:\r\n\r\n{# src/Acme/DemoBundle/Resources/views/Demo/hello.html.twig #}\r\n  {% extends "AcmeDemoBundle::base.html.twig" %}\r\n  {% block title “Hello” name %}\r\n    {% block content %}\r\n      <h1>Hello {{ name }}!</h1>\r\n  {% endblock %}\r\n\r\n{# src/Acme/DemoBundle/Resources/views/base.html.twig #}\r\n<div class="symfony-content">\r\n  {% block content %}\r\n  {% endblock %}\r\n</div>\r\n\r\nIncluyendo otras plantillas\r\nLa mejor manera de compartir un fragmento de código entre varias plantillas diferentes es crear una nueva plantilla,\r\nque luego puedas incluir en otras plantillas.\r\n\r\nCrea una plantilla embedded.html.twig:\r\n{# src/Acme/DemoBundle/Resources/views/Demo/embedded.html.twig #}\r\nHello {{ name }}\r\n\r\n{# src/Acme/DemoBundle/Resources/views/Demo/hello.html.twig #}\r\n{% extends "AcmeDemoBundle::base.html.twig" %}\r\n{# sustituye el bloque ’content’ por embedded.html.twig #}\r\n  {% block content %}\r\n    {% include "AcmeDemoBundle:Demo:embedded.html.twig" %}\r\n  {% endblock %}\r\n', '2015-06-30');
INSERT INTO `table_trucos` (`f_id_trucos`, `f_titulo_trucos`, `f_familia_trucos`, `f_codigo_truco`, `fecha`) VALUES
(432, 'Symfony Twig', 'TWIG', '{% for i in 0..10 %}\r\n    * {{ i }}\r\n{% endfor %}\r\n\r\n{% for alumno in alumnos %}  \r\n  <div> {{alumno.matricula}}  - {{alumno.nombre}}  <div>  \r\n{% endfor %}\r\n\r\n\r\n <p>\r\n   Fecha de hoy: {{ "now" | date ("m/d/Y") }}\r\n <p>\r\n\r\n\r\n<div> {{alumno.matricula}}  - {{alumno.nombre}}  <div>\r\n\r\n{% if users %}\r\n    <ul>\r\n        {% for user in users %}\r\n            <li>{{ user.username|e }}</li>\r\n        {% endfor %}\r\n    </ul>\r\n{% endif %}\r\n\r\n{% set items = [''a'', ''b'', ''c'', ''d'', ''e'', ''f'', ''g''] %}\r\n\r\n<table>\r\n{% for row in items|batch(3, ''No item'') %}\r\n    <tr>\r\n        {% for column in row %}\r\n            <td>{{ column }}</td>\r\n        {% endfor %}\r\n    </tr>\r\n{% endfor %}\r\n</table>\r\n\r\n{% include ''header.html'' %}\r\n    Body\r\n{% include ''footer.html'' %}\r\n', '2015-06-30'),
(433, 'Symfony - Doctrine', 'SYMFONY', 'public function mytestAction($nombreProfesor)\r\n    {\r\n        $profesor = $this->getDoctrine()\r\n        ->getRepository(''marlonMainBundle:Profesores'')\r\n        ->findOneByNombre($nombreProfesor);\r\n\r\n        if (!$profesor) {\r\n        throw $this->createNotFoundException(\r\n            ''No product found ''.$nombreProfesor\r\n        );  }\r\n    else  {\r\n    	echo "El nombre es:".$profesor->getNombre()."y la edad:".$profesor->getEdad();\r\n    }\r\n    return $this->render(''marlonMainBundle:Default:mytest.html.twig'');\r\n    }\r\n\r\n\r\n$repository = $this->getDoctrine()\r\n    ->getRepository(''AcmeStoreBundle:Product'');\r\n\r\n// consulta por la clave principal (generalmente ''id'')\r\n$product = $repository->find($id);\r\n\r\n// métodos con nombres dinámicos para buscar un valor en función de alguna columna\r\n$product = $repository->findOneById($id);\r\n$product = $repository->findOneByName(''foo'');\r\n\r\n// obtiene todos los productos\r\n$products = $repository->findAll();\r\n\r\n// busca productos basándose en el valor de una columna\r\n$products = $repository->findByPrice(19.99);\r\n\r\n// obtiene todos los productos con un nombre determinado\r\n// y ordena los resultados por precio\r\n$product = $repository->findBy(\r\n    array(''name''  => ''foo''),\r\n    array(''price'' => ''ASC''));\r\n\r\n', '2015-06-30'),
(434, 'Crear un Bundle', 'SYMFONY', '> cd c:/xampp/htdocs/symfony\r\n> php app/console generate:bundle\r\n> marlon\\ejemploBundle\r\n> yml\r\n> no\r\n> ENTER...ENTER\r\n', '2015-06-30'),
(435, 'Crear un servidor virtual', 'SYMFONY', '> cd c:/xampp/htdocs/symfony\r\n> php app/console server:run\r\nhttp://localhost:8000/', '2015-06-30'),
(436, 'Ejecutar Bundle', 'SYMFONY', 'http://localhost/MySymfony/Symfony243/web/app_dev.php/hello/Marlon\r\nSymfony243\\src\\marlon\\ejemploBundle\\Resources\\config/routing.yml\r\n', '2015-06-30'),
(437, 'Eliminar un Bundle', 'SYMFONY', '1- Cerrar todos los archivos abiertos\r\n2- src {Borras la carpeta que tiene el Bundle}\r\n3- Symfony/app/AppKernel.php borramos la linea que llama a tu Bundle\r\nnew marlon\\ejemploBundle\\marlonejemploBundle(),\r\n4-Symfony/config/routing.yml y limpiamos el archivo\r\nmarlonejemplo:\r\n    resource: "@marlonejemploBundle/Resources/config/routing.yml" prefix:   /\r\n\r\n', '2015-06-30'),
(438, 'Pasos para adicionar nuevas Vistas', 'SYMFONY', '1-Symfony\\src\\dma\\alumnosBundle\\Resources\\config\r\n{routing.yml} - Configuramos la ruta de las páginas\r\n2-Symfony\\src\\dma\\alumnosBundle\\Controller\r\n{DefaultController.php} - Configuramos el controlador\r\n3-Symfony\\src\\dma\\alumnosBundle\\Resources\\views\\Default\r\n{ejemplostwig.html.twig} Hacemos las vistas', '2015-06-30'),
(439, 'Una petición Symfony en la práctica', 'SYMFONY', '# app/config/routing.yml\r\ncontacto:\r\n    path:     /contacto\r\n    defaults: { _controller: AcmeDemoBundle:Main:contacto }\r\n\r\n// src/Acme/DemoBundle/Controller/MainController.php\r\nnamespace Acme\\DemoBundle\\Controller;\r\n \r\nuse Symfony\\Component\\HttpFoundation\\Response;\r\n \r\nclass MainController\r\n{\r\n    public function contactoAction()\r\n    {\r\n        return new Response(''<h1>Contáctanos</h1>'');\r\n    }\r\n}', '2015-06-30'),
(440, 'Cambiando a entorno de produccion', 'SYMFONY', '# web/app.php\r\n $kernel = new AppKernel(''prod'', false);\r\n $kernel = new AppKernel(''prod'', true);\r\n\r\n', '2015-06-30'),
(441, 'Limpiar el cache', 'SYMFONY', 'php app/console cache:clear --env=prod\r\n', '2015-06-30'),
(442, 'Instala los archivos CSS y JavaScript utilizados por la    interfaz del bundle:', 'SYMFONY', 'php app/console assets:install --symlink\r\n', '2015-06-30'),
(443, 'Actualizar Composer', 'SYMFONY', 'composer update --prefer-source --prefer-dist\r\ncomposer install --prefer-source --prefer-dist', '2015-06-30'),
(444, 'Symfony (Doctrine) Crear Base de Datos', 'SYMFONY', '1- Creamos la Base de Datos desde Doctrine\r\n > php app/console doctrine:database:create\r\n\r\n2- Creamos la entidad y la tabla \r\n > php app/console doctrine:generate:entity\r\n > Name: CursoMainBundle:Ciudad\r\n > ENTER\r\n > New field name: nombre\r\n > empty class: no \r\n > Yes\r\n\r\n3- Creamos las tablas en la base de datos\r\n > php app/console doctrine:schema:create --dump-sql\r\n > php app/console doctrine:schema:create\r\n\r\n\r\n4- Creamos el CRUD\r\n > php app/console generate:doctrine:crud\r\n\r\n5- Actualizar las base de datos desde la Entidad\r\n > php app/console doctrine:schema:update', '2015-06-30'),
(445, 'Trabajar con formularios ', 'SYMFONY', 'FormController\r\n<?php\r\nnamespace marlon\\FormBundle\\Controller;\r\nuse Symfony\\Bundle\\FrameworkBundle\\Controller\\Controller;\r\nuse Symfony\\Component\\HttpFoundation\\Request;\r\nclass FormController extends Controller\r\n{\r\n    public function formAction(Request $request)\r\n    {\r\n        if($request->getMethod()=="POST")\r\n          	{\r\n          		$numero1 = $request->get("numero1");\r\n          		$numero2 = $request->get("numero1");\r\n          		$suma = $numero1 + $numero2;\r\n          		echo "<h2>La suma es:".$suma."</h2>";\r\n          		exit;\r\n          		}\r\n        return $this->render(''marlonFormBundle:Form:form.html.twig'');\r\n    }\r\n}\r\n\r\nform.html.twing\r\n<h2>Sumar dos numeros</h2>\r\n<form action="" method="POST">\r\n    <input type="text" name="numero1" value="1">\r\n  <br>\r\n    <input type="text" name="numero2" value="2"> \r\n  <br>\r\n    <input type="submit" value="sumar">\r\n</form>\r\n\r\nrouting.yml\r\nmarlon_form_form:\r\n    pattern:  /form\r\n    defaults: { _controller: marlonFormBundle:Form:form }\r\n', '2015-06-30'),
(446, 'Login', 'SYMFONY', 'Symfony (Security.YML)  /*  ******************************************************************************************** */\r\n# app/config/security.yml\r\nsecurity:\r\n    encoders:\r\n        Symfony\\Component\\Security\\Core\\User\\User: plaintext\r\n\r\n    role_hierarchy:\r\n        ROLE_ADMIN:       ROLE_USER\r\n        ROLE_SUPER_ADMIN: [ROLE_USER, ROLE_ADMIN, ROLE_ALLOWED_TO_SWITCH]\r\n\r\n    providers:\r\n        in_memory:\r\n            memory:\r\n                users:\r\n                    user:  { password: userpass, roles: [ ''ROLE_USER'' ] }\r\n                    admin: { password: adminpass, roles: [ ''ROLE_ADMIN'' ] }\r\n\r\n    firewalls:\r\n        dev:\r\n            pattern:  ^/(_(profiler|wdt)|css|images|js)/\r\n            security: false\r\n\r\n        login:\r\n            pattern:  ^/backend/login$\r\n            security: false\r\n\r\n        secured_area:\r\n            pattern:    ^/backend\r\n            form_login:\r\n                check_path: /backend/login_check\r\n                login_path: /backend/login\r\n            logout:\r\n                path:   /backend/logout\r\n                target: /backend/login\r\n            #anonymous: ~\r\n            #http_basic:\r\n            #    realm: "Secured Demo Area"\r\n\r\n    access_control:\r\n        #- { path: ^/login, roles: IS_AUTHENTICATED_ANONYMOUSLY, requires_channel: https }\r\n\r\n\r\nSymfony (AdminController.php) /***********************************************************************************************************************************************************/\r\n<?php\r\nnamespace marlon\\FormBundle\\Controller;\r\nuse Symfony\\Bundle\\FrameworkBundle\\Controller\\Controller;\r\nuse Symfony\\Component\\HttpFoundation\\Request;\r\nuse Symfony\\Component\\Security\\Core\\SecurityContext;\r\nclass AdminController extends Controller\r\n{\r\n    public function indexAction()\r\n    {\r\n        return $this->render(''marlonFormBundle:Admin:index.html.twig'');\r\n    }\r\n    public function loginAction(Request $request)\r\n    {\r\n        $session = $request->getSession(); \r\n        // get the login error if there is one\r\n        if ($request->attributes->has(SecurityContext::AUTHENTICATION_ERROR)) {\r\n            $error = $request->attributes->get(\r\n                SecurityContext::AUTHENTICATION_ERROR\r\n            );\r\n        } else {\r\n            $error = $session->get(SecurityContext::AUTHENTICATION_ERROR);\r\n            $session->remove(SecurityContext::AUTHENTICATION_ERROR);\r\n        } \r\n        return $this->render(\r\n            ''marlonFormBundle:Admin:login.html.twig'',\r\n            array(\r\n                // last username entered by the user\r\n                ''last_username'' => $session->get(SecurityContext::LAST_USERNAME),\r\n                ''error''         => $error,\r\n            )\r\n        );\r\n    }\r\n}\r\n\r\n\r\nSymfony (Ruting.yml) /***************************************************************************************************************************/\r\nmarlon_form_homepage:\r\n    pattern:  /hello/{name}\r\n    defaults: { _controller: marlonFormBundle:Default:index }\r\n\r\nmarlon_form_form:\r\n    pattern:  /form\r\n    defaults: { _controller: marlonFormBundle:Form:form }\r\n\r\nmarlon_form_admin:\r\n    pattern:  /backend/admin\r\n    defaults: { _controller: marlonFormBundle:Admin:index }\r\n\r\nmarlon_form_login:\r\n    pattern:  /backend/login\r\n    defaults: { _controller: marlonFormBundle:Admin:login }\r\n\r\nlogin_check:\r\n    pattern:   /backend/login_check\r\n\r\nlogout:\r\n    pattern: /backend/logout\r\n\r\n', '2015-06-30'),
(447, 'Symfony (Twig)', 'SYMFONY', 'Funcion assets enlazar css\r\n<link href="{{ asset(’css/blog.css’) }}" rel="stylesheet" type="text/css" />\r\n\r\nFuncion assets enlazar imagenes\r\n<img src="{{ asset(’images/logo.png’) }}" />\r\n<img src="{{ asset(’bundles/cursomain/images/logo.png’) }}" ></img>\r\n\r\nAssets -> web\r\n\r\nMainBundle/Resources/public/css or img \r\n\r\nphp app/console assets:install web\r\n\r\nphp app/console assets:install --symlink  \r\n\r\n$ php app/console assets:install --symlink --relative\r\n\r\nLa función path sabe cómo generar URL basándose en la conﬁguración de enrutado. De esta manera,\r\ntodas tus URL se pueden actualizar fácilmente con sólo cambiar la conﬁguración:\r\n\r\n<a href="{{ path(’_demo_hello’, { ’name’: ’Thomas’ }) }}">Greet Thomas!</a> \r\n\r\n\r\nHerencia de plantillas\r\n{% extends "::base.html.twig" %}\r\n{% block stylesheets %}\r\n   <link href="{{ asset(''bundles/MainBundle/css/bootstrap.min.css'') }}" rel="stylesheet" type="text/css" />\r\n   <link href="{{ asset(''bundles/MainBundle/css/blog.css'') }}" rel="stylesheet" type="text/css" />\r\n{% endblock %}', '2015-06-30'),
(448, 'Como hacer traduciones en Symfony ', 'SYMFONY', '1- Tienes que ir al fichero: C:\\xampp\\htdocs\\Symfony243\\app\\config\\config.yml \r\n\r\n    translator: { fallback: es }\r\n\r\n2- Creas una carpeta Resources/translations - Creas un archivo con el nombre messages.es.yml\r\n\r\n#C:\\xampp\\htdocs\\Symfony243\\src\\marlon\\webkodeBundle\\Resources\\translations\\messages.es.yml\r\n\r\n''Symfony2 is Great'': Symfony es Genial\r\n\r\n3 - Creas la Plantilla\r\n\r\n{# extends ''::base.thml.twig'' #}\r\n\r\n{% block body %}\r\n    <H2> Ayuda <H2>\r\n    <h3> {{ ''Symfony2 is Great'' |trans }}\r\n{% endblock %}', '2015-06-30'),
(449, 'Symfony -  Herencia de Plantillas ', 'SYMFONY', 'C:\\xampp\\htdocs\\Symfony243\\app\\Resources\\views\\mybase.html.twig\r\n\r\n{% extends ''::base.html.twig'' %}\r\n\r\n {% block stylesheets %}\r\n     <link href="{{ asset(''bundles/marlon/css/bootstrap.min.css'')}}" type="text/css" rel="stylesheet" />\r\n\r\n {% endblock %}\r\n  \r\n {% block javascripts %}\r\n    <script src="{{ asset(''bundles/marlon/js/bootstrap.min.js'') }}" type="text/javascript"></script>  \r\n    <script src="{{ asset(''bundles/marlon/js/main.js'') }}" type="text/javascript"></script>  \r\n{% endblock %}   \r\n\r\n\r\nC:\\xampp\\htdocs\\Symfony243\\src\\marlon\\webkodeBundle\\Resources\\views\\Default\\ayuda.html.twig\r\n\r\n{% extends ''::mybase.html.twig'' %}\r\n{% block body %}\r\n    <H2> Ayuda <H2>\r\n    <h3> {{ ''Symfony2 is Great'' |trans }}  \r\n{% endblock %}\r\n\r\n\r\nC:\\xampp\\htdocs\\Symfony243\\web\\bundles\\marlon\\css\\main.css\r\nC:\\xampp\\htdocs\\Symfony243\\web\\bundles\\marlon\\js\\bootstrap.min.js', '2015-06-30'),
(450, 'Symfony Distribuir ', 'SYMFONY', '1. Una vez que tengas ya tu proyecto listo , a nivel local debes destruir la base de datos que tengas creada.\r\n\r\n2. Luego debemos crear una carpeta en la raiz del hosting para almacenar todo el proyecto, ya que en la parte publica del hosting ira todos los archivos que estan en la carpeta web del proyecto. Lo que he realizado es vaciar el contenido de la carpeta web en el public_html, y consecuentemente elimino la carpeta web ya que no servira de nada haciendo esto.\r\n\r\n3. Luego de ya creada la base de datos procedemos a modificar los valores en el archivo parameter en donde le colocamos los valores como son nombre de la base de datos que se creo en el hosting, el nombre del usuario y el password. Lo referente al manejo y configuracion para el gestor de correo no tengo informacion al respecto pues no he llegado hasta ahi.\r\n\r\n4. ahora viene una lista de archivos a borrar.\r\nEn la carpeta publica eliminamos: App_dev.php , el config.php\r\nEn la carpeta app del proyecto eliminamos: El contenido de la carpeta cache, el contenido de la carpeta logs,\r\nEn la carpeta src eliminamos la carpeta Acme\r\nDentro de la carpeta App/config eliminamos: config_dev.yml, config_test.yml, routing_dev.yml , se debe tener presente que las rutas de su proyecto en i caso lo he hecho estan dentro del archivo routing.yml asi como las modificaciones en el config.yml\r\nEliminamos el archivo check.php que esta dentro de la carpeta app.\r\n\r\n5. Ahora viene una lista de archivos a modificar.\r\nEl archivo app.php que esta en la carpeta publica , en donde indican las 3 primeras lineas, debemos adecuar la direccion para que pueda localizar la carpeta del proyecto en donde debes tantos  /../  para poder el controlador frontal encontrar el proyecto. ahora en la linea que dice\r\n$kernel = new AppKernel(''prod'', false);\r\ndebemos cambiar el false por true quedando asi\r\n$kernel = new AppKernel(''prod'', true);\r\n\r\nEn caso de querer puedes habilitar se hace eliminando las barras de comentarios , la tercera linea donde hace referencia a AppCache.php y ademas la que dice $kernel = new AppCache($kernel);  no tengo aun conocimiento de la funcion que cumplen, asi que personalmente aun no las he habilitado.\r\n\r\nYa con esto queda la aplicacion lista. Ahora si no les ingresa a la pagina como fue mi caso, deben verificar si les ingresa colocando en la direccion del navegador el nombre del controlador frontal, si ven que ahi si les ingresa a la pagina significa que tienen que desactivar la reescritura de las direcciones asi que vayan al archivo .htacces que esta en la carpeta publica y modifique la linea que dice \r\n   RewriteEngine On\r\ncambienla que quede\r\n    RewriteEngine Off\r\n\r\nahora tener presente no cambiar el nombre del controlador app.php por el de index deben mantenerlo asi ya que de lo contrario podran ingresar al pagina principal pero no la de los proyectos.\r\n\r\nAhora en vista de que ya les aparece correctamente en los enlaces la direccion completa incluyendo el nombre del controlador se presenta otra cosa y es que si ingresan en la direccion web principal del proyecto no les ingresa por que el busca un archivo index.php o index.html y en el caso el controlador es app.php. Para solucionar esto y considero que es un hack hacerlo pero por el momento no he encontrado alguna solucion mejor deben crear un archivo llamado index.html dentro de la carpeta publica y colocar dentro la instruccion de redireccion como esta\r\n\r\n<html>\r\n<head>\r\n<meta http-equiv="Refresh" content="0;URL=http://direccionwebdelproyecto.com/app.php" />\r\n</head>\r\n<body>\r\n</body>\r\n\r\n', '2015-06-30'),
(451, 'Symfony listar una tabla', 'SYMFONY', 'DefaultController.php\r\n public function listaAction()\r\n    {\r\n        $datos= $this->getDoctrine()\r\n              ->getRepository(''marlonMainBundle:Alumnos'')\r\n              ->findAll();       \r\nreturn $this->render(''marlonMainBundle:Default:lis.html.twig'',\r\n                                                                                                                         compact("datos"));\r\n    }\r\n\r\nLis.html.twig\r\n<h1>  Lista de alumnos </h1>\r\n<ul>\r\n   {% for dato in datos %}\r\n    <li> {{ dato.nombre }} - {{ dato.edad }} </li>\r\n   {%  endfor %}\r\n</ul>\r\n', '2015-06-30'),
(452, 'Symfony detalle de una tabla', 'SYMFONY', 'DefaultController.php\r\npublic function detalleAction($id)\r\n    {\r\n    	$datos= $this->getDoctrine()                     ->getRepository(''marlonMainBundle:Alumnos'')\r\n                   ->find($id);\r\n\r\nif (!$datos)\r\n        {\r\n        	throw $this->createNoFoundException(\r\n                   ''No se encuentra el producto con ese''.$id\r\n        		);        	\r\n        }               \r\n        return $this->render(''marlonMainBundle:Default:detalle.html.twig'',compact("datos"));\r\n    }\r\n\r\n\r\nLita.html.twig\r\n<h1>  Detalle alumnos </h1>\r\n  {{ datos.nombre }} - {{ datos.edad }}\r\n\r\n<a href="{{ asset(''lis'')}}"> Volver </a>', '2015-06-30'),
(453, 'Que estudiar C#', 'C#', 'Microsoft Certified Professional (MCP). “Programming in HTML5 with JavaScript and CSS3”. \r\n  -  Microsoft Certified Professional (MCP). “Developing ASP.NET MVC 4 Web Applications”. \r\n  -  Miami-Date County Public School (MDCPS), "Web Developer" certification. \r\n\r\n\r\nEntity Framework  - Framework - Entidatamodel para usarlo\r\n\r\nWindows Presentation Foundation\r\n\r\nEntity Framework (EF) es un asignador objeto-relacional que permite a los desarrolladores de .NET trabajar con datos relacionales usando objetos específicos del dominio. Elimina la necesidad de la mayor parte del código de acceso a datos que los desarrolladores suelen tener que escribir.\r\n', '2015-06-30'),
(454, 'Clases', 'JAVA', 'public class Rectangulo {\r\n    int x;\r\n    int y;\r\n    int ancho;\r\n    int alto;\r\n    public Rectangulo() {\r\n        x=0;\r\n        y=0;\r\n        ancho=0;\r\n        alto=0;\r\n    }\r\n    public Rectangulo(int x1, int y1, int w, int h) {\r\n        x=x1;\r\n        y=y1;\r\n        ancho=w;\r\n        alto=h;\r\n    }\r\n    public Rectangulo(int w, int h) {\r\n        x=0;\r\n        y=0;\r\n        ancho=w;\r\n        alto=h;\r\n    }\r\n    int calcularArea(){\r\n	return (ancho*alto);\r\n   }\r\n   void desplazar(int dx, int dy){\r\n	x+=dx;\r\n	y+=dy;\r\n   }\r\n   boolean estaDentro(int x1, int y1){\r\n	if((x1>x)&&(x1<x+ancho)&&(y1>y)&&(y1<y+ancho)){\r\n		return true;\r\n	}\r\n	return false;\r\n   }\r\n}\r\n\r\n\r\n\r\n\r\n\r\nRectangulo rect1=new Rectangulo(100, 200);\r\n\r\n\r\n', '2015-06-30'),
(455, 'Ciclo FOR', 'JAVA', 'for (int i = 0; i < 10; i++) {\r\n        System.out.println(i);\r\n    } \r\n\r\npublic class FactorialApp {\r\n    public static void main(String[] args) {\r\n        int numero=4;\r\n        long resultado=1;\r\n        for(int i=1; i<=numero; i++){\r\n            resultado*=i;\r\n        }\r\n        System.out.println("El factorial es "+resultado);\r\n     }\r\n}', '2015-06-30'),
(456, 'switch', 'JAVA', 'switch(expresion){\r\n		case valor1:\r\n			sentencia1;\r\n			break;			//sale de switch\r\n		case valor2:\r\n			sentencia2;\r\n			break;			//sale switch\r\n		case valor3:\r\n			sentencia3;\r\n			break;			//sale de switch\r\n		default:\r\n			sentencia4;\r\n	}\r\n\r\n\r\npublic class SwitchApp1 {\r\n    public static void main(String[] args) {\r\n        int mes=3;\r\n        switch (mes) {\r\n            case 1:  System.out.println("Enero"); break;\r\n            case 2:  System.out.println("Febrero"); break;\r\n            case 3:  System.out.println("Marzo"); break;\r\n            case 4:  System.out.println("Abril"); break;\r\n            case 5:  System.out.println("Mayo"); break;\r\n            case 6:  System.out.println("Junio"); break;\r\n            case 7:  System.out.println("Julio"); break;\r\n            case 8:  System.out.println("Agosto"); break;\r\n            case 9:  System.out.println("Septiembre"); break;\r\n            case 10: System.out.println("Octubre"); break;\r\n            case 11: System.out.println("Noviembre"); break;\r\n            case 12: System.out.println("Diciembre"); break;\r\n            default: System.out.println("Este mes no existe"); break;\r\n        }\r\n    }\r\n}', '2015-06-30'),
(457, 'IF ELSE', 'JAVA', '  if (numeroBoleto==numeroSoreteo) \r\n                 {\r\n		    premio=1000;\r\n                   }\r\n	else {\r\n		 premio=0;\r\n                }\r\n\r\nen una sola línea\r\n\r\n	premio=(numeroBoleto==numeroSoreteo) ? 1000 : 0;', '2015-06-30'),
(458, 'Comentarios', 'JAVA', ' /*--------------------------------|\r\n|   (C) Marlon                       |\r\n|   fecha: Marzo 1980           |\r\n|   programa: PrimeroApp    |\r\n|---------------------------------*/\r\n\r\n//System.out.println("El primer programa");\r\n', '2015-06-30'),
(459, 'Tipos de Datos', 'JAVA', 'byte\r\nshort\r\nint\r\nlong\r\nfloat\r\ndouble\r\nchar\r\nboolean', '2015-06-30'),
(460, 'Instalar ABAP en Eclipse', 'ABAP SAP', 'Cómo instalar el "plugin" ABAP Development Tools\r\n\r\nAntes de instalar las ADT, es necesario:\r\n\r\n1) Descargar e instalar Eclipse en sí mismo (por ejemplo, la versión Kepler o la más nueva Luna) desde el sitio eclipse.org. Una vez descargado y descomprimido, en la carpeta Eclipse, ejecutar eclipse.exe. Luego, en el menú, elegir Help → Install New Software\r\n\r\n2) Para instalar el ADT, conocer los prerequisitos y las instrucciones de instalación en: https://tools.hana.ondemand.com/#abap . Este sitio se mantiene actualizado por SAP, y es el punto central y la manera más fácil de descargar e instalar cualquier "plugin" para las diiferentes tecnologías de desarrollo SAP: ABAP, SAPUI5 o SAP HANA Studio.\r\n\r\nEs muy importante leer los prerequisitos de instalación para asegurarse que pueda ser instalado el plugin adecuadamente.\r\n\r\n2 a) Si se quiere instalar el plugin para operar con la versión Kepler de Eclipse: Agregar https://tools.hana.ondemand.com/kepler en el campo "Work with" y presionar Enter. Aparecerá una pantalla como la siguiente, en donde se deberá elegir en este caso: ABAP Development Tools for SAP Netweaver y presionar Next, y luego presionar Finish, en la próxima pantalla:', '2015-06-30'),
(461, 'Introduccion ABAP Comandos', 'ABAP SAP', '1.1. TRANSACCIONES PRINCIPALES:\r\n\r\nEl Abap/4 es el lenguaje de programación de SAP R/3. Se llega al editor por menú con: Herramientas – Workbench Abap 4. Con esto se llega a las transacciones principales del conjunto de herramientas (“workbench”) del Abap/4:\r\n\r\n    Object Browser: Se utiliza para localizar y ver los objetos creados por el usuario, datos, programas, ... Se ven en forma arbórea. Permite editar los objetos, visualizarlos, crearlos, ejecutarlos, moverlos, buscarlos, ... Hay que activar todos los objetos creados antes de usarlos.\r\n    Diccionario de Datos (D.D.): Sirve para crear los tipos y objetos de datos de SAP, estructuras, bases de datos, ... cada uno identificado con su nombre. Con la transacción SE11 se visualizan los objetos de Diccionario de Datos. Para entrar por menú: Herramientas – Workbench Abap 4 – Dictionary (F6), o bien la transacción SE11.\r\n    Editor Abap/4:\r\n    Para codificar los programas. Éstos se pueden visualizar de distintas formas (se eligen en el menú de Opciones). Conviene que el nombre de los objetos nuevos que se creen empiece por Y o por Z, para que así una nueva versión de SAP los respete y no sean machacados.\r\n    Para modificar un programa conservando el original, haciendo una copia del mismo. Los programas fuente pueden bajarse a fichero de texto y luego recuperarlos con Utilidades – Upload / Download. Con CTRL+Y se puede marcar un rectángulo de la pantalla, y copiarlo.\r\n    Todo objeto debe tener asignada una clase de desarrollo (son agrupaciones de objetos), para poder luego transportarlo a la máquina de producción (con la que trabaja el usuario), desde la máquina de desarrollo (la del programador).\r\n    Al crear objetos no locales también hay que ‘guardarlos’ en una orden de transporte, donde se graban. Las órdenes se pueden crear, liberar, bloquear, ... Los objetos locales (de la clase local $TMP) NO se pueden transportar.\r\n    Jerarquía: Los objetos pertenecen a una orden de corrección o tarea, y esta a su vez a una orden de transporte.\r\n    Se pueden crear (y borrar) nuevos modos de pantalla (hasta un máximo de 6 simultáneos. Los modos son nuevas ventanas de Windows), con la opción de menú Sistema – crear modo.\r\n    Para crear nuevas líneas en el editor hay que crearlas partiéndolas (con el botón Partir o F7), en donde se desea insertar; o bien poniendo In como nº de línea, con n el nº líneas a insertar.\r\n    Desde una palabra de un programa, desde el editor de Abap/4, se puede navegar a cualquier otro sitio haciendo doble clic.\r\n    Menú Painter:Sirve para diseñar menús, con sus teclas de función, botones, iconos, y funciones estándar (save, exit, back, cancel, print, find, ...).\r\n    Biblioteca de funciones: Para testar funciones, editarlas, ... Las funciones tienen código fuente Abap, parámetros de entrada, parámetros de salida (que pueden ser tablas también). Ejemplos de funciones: cuadros de diálogo (‘popups’), rh_get_date_dayname (devuelve el día de la semana de una fecha dada), ...\r\n    Screen Painter:Sirve para diseñar pantallas y formularios. Hay marcos, botones, iconos, ... Es de tipo visual. El número de dynpro (un dynpro es cada pantalla de un programa, que puede tener varias) identifica el número de pantalla. Para ver el diseño de la pantalla (‘layout’), pulsar el botón Full Screen.\r\n    Modelo: Botón que se usa para escribir esqueletos de sentencias, y así no tener que teclearla completamente. Los parámetros que tenga opcionales aparecen como comentarios.\r\n\r\n \r\n1.2. Otras transacciones:\r\n\r\n    Data Browser: Para ver el contenido de una tabla (por ejemplo, la tabla de mensajes T100). Para llegar por menú: Herramientas – Workbench Abap/4 – Resumen – Data Browser, o bien con la transacción SE16.\r\n    Actualización de tablas: Para editar y tratar tablas (las que sean modificables).Para llegar por menú: Sistema – Servicios – Actualizar tabla, o bien las transacciones SM30 y SM31(ambas son similares).\r\n    Al grabar la tabla, dar orden de transporte. No es necesario indicar la orden de corrección o tarea (como cualquier otro objeto de SAP).\r\n    Tablas de customizing: Son tablas parametrizables. Así SAP tiene más flexibilidad.\r\n    Actualizar una vista: Una vista es una forma de visualizar ciertos campos de una tabla o varias relacionadas, sólo los que interesen. Una tabla puede tener varias vistas asociadas.\r\n    Sistema Info: Informe para ver todos los objetos SAP (tablas, elementos de datos, ...) de un tipo (char, fecha, numérico, ...). Para llegar por menú: Herramientas – Workbench Abap/4 – Resumen – Sistema Info.\r\n    Menús de Ámbito: Para crear un menú de ámbito para el usuario, con iconos, botones, teclas de función, ... Para llegar por menú: Herramientas – Workbench Abap/4 – Desarrollo – Más herramientas – Menú de ámbito.\r\n    Códigos de transacción: Para crear transacciones nuevas, usando otras ya existentes (como una macro) o totalmente nuevas. Para llegar por menú: Herramientas – Workbench Abap/4 – Desarrollo – Más herramientas – Transacciones.\r\n    Bases de datos lógicas:Transacción para crear, ver, ... bases de datos lógicas. Para llegar por menú: Herramientas – Workbench Abap/4 – Desarrollo – Entorno de programación – Bases de datos lógicas.\r\n    Textos breves y explicativos: Sirve para traducir mensajes (se debe indicar el idioma). Ruta de acceso por menú: Herramientas – Workbench Abap/4 – Utilidades – Traducción – Textos breves y explicativos.\r\n    Documentación R/3: Para documentar objetos de SAP R/3. Para llegar por menú, ir a la opción: Herramientas – Workbench Abap/4 – Utilidades – Documentación.\r\n    Editor Split Screen: Parte la pantalla en dos. Su utilidad principal es comparar el código fuente de dos programas (buscar diferencias, ...). Para llegar por menú: Herramientas – Workbench Abap/4 – Desarrollo – Entorno de programación – Editor Split Screen.\r\n    Módulos de diálogo: Son similares a los módulos de funciones. Tienen parámetros de entrada, de salida, y de entrada / salida. Para cuadros de diálogo. Para llegar por menú: Herramientas – Workbench Abap/4 – Desarrollo – Entorno de programación – Módulos de diálogo.\r\n    Mensajes de usuario: Los mensajes son objetos SAP. Para mostrar un mensaje (desde un programa) basta con indicar laclase de mensaje, su tipo y su número.\r\n    La clase de mensaje es una tabla donde se crean y almacenan agrupados todos los mensajes de esa clase. Es el ID, como ZH, por ejemplo. Las clases de mensajes no estándar deben empezar por Z, y su nombre sólo tiene 2 caracteres. Hay varias clases estándar, como HB. Tabla de mensajes del sistema: T100.\r\n    El tipo de mensaje (se especifica al lanzar el mensaje, no es un atributo propio del mismo). Hay 5 tipos de mensajes: E (Error). El programa se queda bloqueado; hay que salir con /N en la línea de transacciones. W (Warning). Tras leer el mensaje, si era en el listado básico, se termina el report; si era en el listado secundario, va al listado primario.I (Informativo). Tras leerse el mensaje, sigue el programa. S (Success): Muestra el mensaje en la siguiente pantalla a la actual. A (Abort). Se muestra el mensaje y su número, y tras leerlo acaba el programa.\r\n    El número del mensaje es su identificador dentro de la clase de mensaje. Los mensajes se almacenan aparte del código fuente del programa (en su clase de mensajes), para reutilizarlos. Para visualizar por pantalla los mensajes, la ruta por menú es: Herramientas – Workbench Abap/4 – Desarrollo – Entorno de programación – Mensajes (o bien: transacción SE91).\r\n    Se pueden “parametrizar”: para ello se escribe dentro del texto del mensaje el carácter & (hasta 4 veces como máximo: se permiten 4 parámetros máximo). Al mostrarlo en pantalla, con MESSAGE … WITH pueden darse valores actuales a los & formales: se sustituyen en orden.\r\n\r\n    Ejemplos:\r\n\r\n    REPORT name MESSAGE-ID clase_mensaje.  "Clase por defecto en ese report.\r\n\r\n    *T: tipo mensaje, num: mensaje nºnum de la clase definida en la sentencia REPORT. "F1 … F4 son los 4 parámetros posibles.\r\n     MESSAGE_ID id_clase TYPE T NUMBER num [ WITH F1 … F4]. \r\n\r\n    *Abreviatura de la sintaxis anterior\r\n     MESSAGE Tnum [ WITH F1 … F4 ].\r\n\r\n    *Mensaje nº 200, de Error, con un parám\r\n     MESSAGE E200 WITH sy-uname.\r\n                \r\n\r\n         \r\n\r\n    MESSAGE-ID ''HB''TYPE ''E'' NUMBER ''200''. "Abreviado: MESSAGE E200(HB).\r\n\r\n    Sistema de corrección y transporte: Árbol de órdenes de corrección y transporte. Por menú: Herramientas – Workbench Abap/4 – Resumen – Workbench Organizer. (SE09).\r\n\r\n    Jerarquía de aplicación: Árbol de los objetos SAP o de la aplicación del cliente. Con doble clic en algún nivel de la jerarquía, se muestran los objetos (programas, datos, ...) asociados a esa rama. Para llegar por menú: Herramientas – Workbench Abap/4 – Resumen – Jerarquía de aplicación - SAP / Cliente (según corresponda).\r\n\r\n \r\n1.3. Objetos parcialesDEL EDITOR Abap/4:\r\n\r\n    Texto fuente:Programa fuente Abap/4.\r\n    Atributos: Información asociada al programa:\r\n    Creador y Fecha.\r\n    Título (campoObligatorio).\r\n    Tipo de programa:1 para ejecutables (on-line); I para includes (un INCLUDE es un código Abap que se llama desde otros programas, para reutilizarlo. Por sí solo no es ejecutable. Ejemplo: un include para guardar los datos globales, que luego es llamado por el programa principal); M para module pool’s (se llama a través de una transacción. Es como un formulario; no es ejecutable). CampoObligatorio.\r\n    Status del programa:P para los estándar SAP, K para cliente, S para programas del sistema, T para test.\r\n    Aplicación: Limita el módulo de SAP en el que puede ejecutarse el programa. Un asterisco indica que el programa sirve para todos los módulos.CampoObligatorio.\r\n    Grupo de autorizaciones: Clave para proteger el programa frente a otros usuarios.\r\n    Checkboxs adicionales en la pantalla de atributos:\r\n    Mayúsculas / Minúsculas: Fuerza la escritura del texto todo en mayúsculas o no.\r\n    Bloqueo del editor: Si está marcado, otro usuario no podrá modificar el programa.\r\n    Ejecución con variante: Fuerza la ejecución del programa con cierta variante\r\n    Cálculo en coma fija.\r\n    Documentación: Información asociada al objeto creado. Se generan campos tipo ‘script’ que se rellenan con lo que corresponda. Ejemplo: &DESCRIPTION&,&PRECONDITION&(parámetros de entrada), &OUTPUT& (salida del sistema), &EXAMPLE& (ejemplos). Hay que entrar por modificar.\r\n    Elementos de texto: Para los distintas etiquetas de texto que puede incluir un programa.\r\n    Título y cabeceras (de los informes). Para crear una cabecera de un listado. Por menú, desde el editor Abap/4: Pasar a – Elementos de texto – Título y cabeceras.\r\n    Símbolos de texto: Para poder reutilizar constantes de texto, se crea un símbolo para cada una. Al usarlo, se escribe en el programa el código del símbolo, ej. ‘001’, y luego aparece en pantalla el texto asociado a ese código. Ejemplo: WRITE TEXT-001. Escribe en pantalla el texto asociado al símbolo 001. Se guardan todos en la tabla TEXT (hay una tabla TEXT por programa en ejecución.), que se accede por programa como cualquier otra tabla: nametable – namefield. En el programa no se deben poner textos literales, sino elementos (símbolos) de texto, para mayor reutilización. Para crearlos, por menú: Pasar a – Elementos de texto – Símbolos de texto. Escribir su número y texto.\r\n    Textos de selección: Nombres de los campos de entrada de la pantalla de selección, para mayor legibilidad del usuario. Por menú, desde el editor Abap/4: Pasar a – Elementos de texto – Textos de selección.\r\n    Variantes: Almacena valores de determinados campos de la pantalla de selección para forzar la ejecución del programa con dichos valores. Un programa puede ejecutarse solo o con alguna variante grabada. Para grabar una variante, pulsar el botón de ejecutar (F8), rellenar los campos con los valores deseados para la variante, y pulsar Pasar a – grabar como variante. Dar un nombre y texto explicativo a la variante. Puede hacerse con esto que algunos campos de la pantalla de selección estén ocultos, sean de sólo lectura, ...\r\n\r\n \r\n1.4. COMANDOS DEL EDITOR:\r\n\r\n    Hay 3 tipos principales de comandos: de cabecera, de línea y del menú de opciones.\r\n    Comandos de cabecera: Se escriben en la barra de comandos del editor (para ello éste debe estar en modo comando).\r\n    Scroll de pantalla: A(TTACH) n (posiciona el cursor en la línea especificada), T(OP) ó – (va a la primera página del programa), B(OTTON) ó ++ (va a la última página del programa), + (página siguiente, como AvPag), - (Página anterior, como RePag).\r\n    Búsqueda: FIND c (busca la primera ocurrencia de la cadena c, a partir del cursor), N(EXT) (busca la siguiente ocurrencia), PRE(VIOUS), R(EPLACE) c1 c2 (reemplaza c1 por c2), F(ETCH) prog (abre en el editor el programa especificado).\r\n    Almacenamiento del programa: S(AVE) (almacena el programa en memoria temporal intermedia), RES(TORE) (trae el programa de memoria intermedia), RES(TORE) AKTIV, U(PDATE) (graba el programa, como F11), SAVEAS prog (graba el programa con un nuevo nombre), PC(DOWN) (download de programa a fichero local).\r\n    Inserción de datos: I (inserta una línea al final del programa), I(NSERT) n (inserta n líneas), IC patrón (inserta el patrón especificado), IC FUNCTION función (inserta el patrón para la función dada), IC SELECT tabla (inserta un SELECT para esa tabla).\r\n    Otros comandos: CH(ECK) (chequea sintaxis del programa actual, como CTRL+F2), PCF(ETCH) (upload de un programa), (H)ELP palabra (muestra ayuda asociada a esa palabra), PP (PRETTY PRINTER, para hacer el programa más legible), PRINT (imprime el listado), RENUM(BER) (numera las líneas de 10 en 10), SHOW nombre (para ver ese objeto), SHOW FUNCTION función, BACK (como F3).\r\n    Comandos del menú de opciones: Se accede a ellos pulsando el botón derecho del ratón en la pantalla del editor. Algunos comandos son los mismos que los de línea. Todos ellos tienen además una tecla de función asociada. La línea “target” del comando será aquella en la que esté el cursor. Ejemplos: Ayuda (F1. Se puede obtener ayuda sobre cualquier campo pulsando F1 con el cursor sobre él. Para obtener más información, pulsar el botón Datos Técnicos. También hay manuales on-line), duplicar (F5), concatenar (F6), partir (F7), ejecutar (F8), grabar (F11), download (MAYS+F8), verificar (CTRL+F2), generar (CTRL+F3), ...\r\n    Comandos de línea: Son los más usados. Se escriben en la zona reservada para los números de línea de las instrucciones de los programas.\r\n    Inserciónde nuevas líneas: S (partir), I (inserta una línea), In (inserta n líneas).\r\n    Borrado de líneas: J (concatenar), D (borra esa línea), DD...DD (borra el bloque).\r\n    Copiado de líneas: C (copia esa línea), CC...CC (copia el bloque), A (inserta “After” la línea o líneas copiadas o movidas),B (como A, pero “Before”).\r\n    Movimiento de líneas: M (mueve la línea), MM...MM (mueve el bloque).\r\n    Repetición de líneas: R(repite esa línea), Rn (repite n líneas), RR...RR (repite el bloque).\r\n    Copiado de líneas al portapapeles del editor. Hay un portapapeles por usuario (esto permite copiar fácilmente texto entre dos modos del editor, si se ha entrado con el mismo usuario, incluso entre diferentes ordenadores) y por cada uno de estos comandos (todos funcionan igual): XX...XX (copia el bloque al portapapeles), X (pega el bloque copiado); YY...YY, Y; WW...WW, W; ZZ...ZZ, Z.\r\n    Otros comandos: CLEAR, RESET (desmarca un bloque, o resetea un comando anterior a medio ejecutar), PR...PR (imprime el bloque), N (inserta un párrafo de comentario), * (scroll de pantalla), > (navegar a un include), < (volver de la navegación a un include), U (sólo para includes), O, T+, B-, O,SH...SH.\r\n\r\nOtros comandos: Son las mismas funcionalidades que los comandos principales.\r\n\r\n    Botones de la barra de menú:Botón Visualizar / Modificar (intercambia entre ambos modos de edición), botón Verificar sintaxis (como CTRL+F2), botón Referencias de utilización (para conocer todos los lugares donde es utilizado el objeto dado), botón Insertar línea, botón Marcar, botón Deshacer, botón Ejecutar programa (como F8), opción de menú de Generar programa (como CTRL + F3. Un programa u otro objeto debe estar generado y activo antes de poder utilizarse), etc.\r\n    Teclas de función:F1 (help), F2 (mouse), F5 (duplicar línea), F6 (concatenar dos líneas del editor), F7 (partir una línea del editor), F8 (ejecutar programa), F9, F11 (grabar), F12 (abandonar el editor), F13 (imprimir. MAYS+F1. Las teclas de función de F13 a F24 se “pulsan” con ayuda del botón MAYS), F14(MAYS+F2), ..., F24(MAYS +F12).\r\n    Opciones de menú:Con la opción de menú Pasar a ... se puede ir a los objetos parciales asociados a un programa, como sus atributos, elementos de texto, ...). Con Utilidades – Upload / Download – Download puede bajarse a fichero de texto local un código fuente de un programa abap/4. Luego puede recuperarse si se desea con Utilidades – Upload / Download – Upload. Con Opciones – modo editor, puede elegirse el modo del editor, uno de estos 3: modo comando (el más habitual), modo PC connumeración de líneas (como el EDIT de MS-DOS, sin los comandos I de insertar línea, ...), modo PC sin numeración de líneas (análogo). También puede elegirse escribir en mayúsculas o en minúsculas todo. Hay otras opciones como verificar, generar, ejecutar, visualizar advertencias, ...\r\n\r\n \r\n1.5. Elementos y componentes del lenguaje:\r\n\r\n    Palabras clave: Cada sentencia empieza por palabra clave (siempre es la 1ª palabra de la sentencia) y acaba en punto. Son de 3 tipos:\r\n    Declarativas: Definen tipos de datos, objetos de datos (constantes y variables. Las constantes alfanuméricas van entre comillas simples), tablas internas, ... Ej.: DATA (para variables locales y tablas internas) TABLES nametable (para tablas del Diccionario de Datos), TYPES (para definir tipos de datos).\r\n    De evento: Definen bloques de proceso que se procesan al activarse cierto evento, como TOP-OF-PAGE (se dispara al generarse cada nueva página de un listado) Controlan el flujo externo. Los programas Abap están dirigidos a eventos, no son secuenciales.\r\n    De control: Unas controlan el flujo interno de programa (como IF,CASE), otras son operacionales (procesan datos, como MOVE, ADD).\r\n    Campos de una tabla: (interna o del Diccionario de Datos). Se acceden escribiendo nombre_tabla – nombre_campo. Ejemplo: SY-SUBRC (en este caso es la tabla del sistema SYST, que se escribe siempre abreviada como SY, con el campo que indica el código de retorno de un programa, función, sentencia, ... Esta tabla contiene otros campos como la fecha actual, hora actual, nombre de usuario, ... Sólo contiene datos en ejecución).\r\n    Comentarios: Los comentarios de línea completa se hacen poniendo un asterisco en la primera columna, y los de línea partida con una comilla doble, hasta EOL (fin de la línea).\r\n    Escritura de un programa: Pueden escribirse varias sentencias en una línea (acabadas en punto y separadas por algún blanco), o una sentencia ocupando varias líneas. Se pueden crear cadenas de sentencias, concatenando varias similares, así: palabra_clave : parte_1, parte_2, ... , parte_n . Se escribe la palabra clave de la sentencia, seguidamente dos puntos, y luego separadas por comas las distintas “subsentencias”. Ejemplo: WRITE: ‘texto1’, ‘texto2’. En lugar de WRITE ‘texto1’. WRITE ‘texto2’.\r\n    Desbloqueo de objetos: Un programa, tabla, dato, … puede quedar bloqueado a consecuencia de una caída del sistema mientras se estaba tratando dicho objeto. Para desbloquearlo, ir por menú a: Herramientas – Gestión – Monitor – Entradas de bloqueo. Dar el nombre del usuario afectado y pulsar el botón de Lista. Borrar (icono de la papelera) la entrada de bloqueo deseada (o bien todas, desde la entrada de menú Borrar – Borrar todas).\r\n\r\n\r\n1.6. OBJETOS DEL DICCIONARIO DE DATOS:\r\n\r\n    El Diccionario de Datos sirve para crear tipos y objetos de datos en SAP, estructuras y bases de datos globales (accesibles desde todos los programas), cada uno identificado con su nombre. Para entrar en el D.D , por menú: Herramientas – Workbench Abap 4 – Dictionary (tecla de función F6), o bien la transacción SE11.\r\n    Los objetos más importantes del Diccionario de Datos (D.D.) son las tablas (no confundir con las tablas internas de los programas). Las tablas del Diccionario se pueden crear, editar, borrar, ... Otros objetos son: estructuras (son como tablas pero sin entradas, no pueden contener datos), vistas, elementos de datos, dominios, objetos matchcode, objetos de bloqueo, ...\r\n    Vistas:Agrupaciones de ciertos campos de tablas, conectados por el mismo objeto, para ver sólo esos. Es útil para ver en una sola “pasada” el contenido de varias tablas relacionadas entre sí.\r\n    Elementos de datos: Sirven para crear campos de tablas. Indican qué “sentido” tiene ese campo. No hace falta crearlos. Se asocian a un dominio, que indica el tipo de dato, valores posibles, … Ejemplo: mandt, para el campo del mandante; también existen elementos de datos predefinidos para monedas, fechas, idiomas, cantidades, … Como todo objeto SAP, antes de poder usarlos hay que activarlos (icono redondo rojo y blanco), para tenerlos disponibles.\r\n    Dominios: Indican el formato de los campos de las tablas. Un campo está asociado a un elemento de datos, y éste a un dominio. Se puede dar valores fijos a un dominio, con Pasar a … – Valores fijos, o bien se le puede dar rangos de valores.\r\n    Objetos de bloqueo: Para evitar conflictos de acceso a una tabla por varios usuarios a la vez. Se usan para hacer zonas de exclusión mutua, semáforos, ...\r\n    Objetos matchcode: No se suelen crear, en cambio sí se crean nuevos conceptos de búsqueda (ID’s: Buscar por nombre, por fechas, por NIF, ...): se crean dándoles un número, descripción, … Éstos sirven para buscar objetos asociados a un campo. Hay muchos ID’s predefinidos. Se pueden definir además restricciones de búsqueda.\r\n    Estructuras: Son similares a tablas, pero no pueden contener datos o entradas., son sólo campos.\r\n    Tablas: Son los objetos más importantes del Diccionario de Datos.\r\n    Tipos de tablas: Las tipo A son de aplicación, las C y G son de Customizing (son para parametrización), las L para datos temporales o de prueba, y las S y W son del sistema.\r\n    Datos definidos en cada campo: En cada campo de una tabla está definido un elemento de datos, el tipo del campo, su longitud, opcionalmente una tabla de verificación (para chequear que el valor del campo está incluido dentro de los posibles definidos en esta tabla), descripción del campo, mandante (simula varias máquinas lógicas, pero puede ser una única máquina física, o varias. Pero los datos de una tabla dependen y están sólo dentro de un mandante. Por eso este dato es obligatorio en todas las tablas).\r\n    Opciones técnicas de una tabla:clase de datos (se suelen usar APPL0 y APPL1), categoría tam. (tamaño esperado de la tabla, en nº entradas), grabar la tabla en MI (memoria intermedia).\r\n    Actualización de tablas: Hay un flag que permite actualizar la tabla si está marcado. Se puede entrar por menú por: Entorno – generar actualización de tabla. Con esta opción se pueden usar las transacciones SM30 o SM31 para actualizarlas (no se podría hacer si no tuviera permiso de actualización y no estuvieran generadas las vistas necesarias para hacerlo). El campo mandanteno es actualizable. Si se añaden campos a una tabla hay que volver a generar las vistas y grupo de funciones para que se tengan en cuenta los cambios.\r\n    Creación de una tabla: Basta pulsar el botón crear e introducir cliente, sociedad, descripción breve (campo obligatorio), nombre (debe empezar por Z), ... El resto de características de la tabla dependerán del elemento de datos (de entre los ya predefinidos, como mandt para el mandante. El elemento de datos debe ajustarse al tipo de datos que necesitemos para un campo dado). En el campo de tabla de verificación el asterisco indica que verifique si se mete dato válido. ‘Clv’ en un campo indica que es clave (pueden ser varios. Dos entradas en una tabla nunca pueden tener igual todos los campos clave). Al grabar la tabla meterla en una clase de desarrollo, como si fuera un programa. Y antes de usarla hay que dar primero las opciones técnicas (clase de datos APPL1 y tamaño 0, por ejemplo) y luego activarla (pulsar el icono circular rojo y blanco). Para permitir actualizaciones, hay que ir por menú a Entorno – generar actualización de tablas. El tipo de actualización puede ser a un nivel, si es que cabe; si no, a 2 niveles. Finalmente pulsar el botón de Proponer imágenes y dar un grupo de funciones (como el ZT01), que tendrá su propia clase de desarrollo. Con esto, haciendo SM31 se pueden meter valores en la tabla (aunque no chequeará si son datos válidossi no está activada la verificación. Para esto, ir por menú a Pasar a … – Claves externas – Propuesta de verificación, una vez por cada campo a verificar).', '2015-06-30'),
(462, 'Crear Programa ABAT', 'ABAP SAP', 'Crear u prorama ABAP SE38', '2015-06-30');
INSERT INTO `table_trucos` (`f_id_trucos`, `f_titulo_trucos`, `f_familia_trucos`, `f_codigo_truco`, `fecha`) VALUES
(463, 'Listar Tabla de SAP Business One con PHP utilizando ODBC . SQLSERVER', 'SQLSERVER', '<!DOCTYPE html>\r\n<html lang="es">\r\n<head>\r\n	<meta charset ="UTF8">\r\n	<title>Ejemplo Boostrap</title>\r\n	<meta name="viewport" content="width = device-width">\r\n	<link href="http://getbootstrap.com/dist/css/bootstrap.min.css" rel="stylesheet">\r\n	<script src="https://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js"></script>\r\n	<script src="http://getbootstrap.com/dist/js/bootstrap.min.js"></script>\r\n\r\n	<!-- HTML5 Shim and Respond.js IE8 support of HTML5 elements and media queries -->\r\n  <!--[if lt IE 9]>\r\n      <script src="https://oss.maxcdn.com/libs/html5shiv/3.7.0/html5shiv.js"></script>\r\n      <script src="https://oss.maxcdn.com/libs/respond.js/1.4.2/respond.min.js"></script>\r\n      <![endif]-->\r\n  </head>\r\n  <body>\r\n  	<div class="container">\r\n  		<div class="tabla">\r\n  			<table class="table table-hover">\r\n  				<thead>\r\n  					<tr>\r\n  						<th>COD</th>\r\n  						<th>NAME</th>\r\n  						<th>PHONE</th>\r\n  						<th>PER CONTACTO</th>\r\n  						<th>MAIL</th>\r\n  					</tr>\r\n  				</thead>\r\n  				<tbody>\r\n<?php\r\n$_TEMP = array();\r\n$_TEMP["server"] = ''PC\\SQLEXPRESS''; //server de base de datos\r\n$_TEMP["database"] = ''SBODemoES''; //nombre de la base de datos\r\n$_TEMP["username"] = ''marlon'';\r\n$_TEMP["password"] = ''pass_ponlo'';\r\n$connection_string = ''DRIVER={SQL SERVER};SERVER=''. $_TEMP["server"] . '';DATABASE='' . $_TEMP["database"];\r\n$conexion = odbc_connect($connection_string, $_TEMP["username"], $_TEMP["password"]);\r\n\r\nunset( $connection_string ); //libera variables\r\nunset( $_TEMP );\r\n\r\n$sql="select * from OCRD";\r\n$resultado = odbc_exec($conexion, $sql);\r\nwhile ($field = odbc_fetch_object($resultado))\r\n{\r\n	echo "<tr>";\r\n	echo "<td>".utf8_encode($field->CardCode)."</td>";\r\n	echo "<td>".utf8_encode($field->CardName)."</td>";\r\n	echo "<td>".utf8_encode($field->Phone1)."</td>";\r\n	echo "<td>".utf8_encode($field->CntctPrsn)."</td>";\r\n	echo "<td>".utf8_encode($field->E_Mail)."</td>";\r\n	echo "</tr>";\r\n}\r\n?>\r\n</div>\r\n</body>', '2015-06-30'),
(464, 'Modulos', 'SAP', 'FI -  Módulo financiero\r\n        Libro Mayor (FI-GL)\r\n        Cuentas a pagar (FI-AP)\r\n        Cuentas a cobrar (FI-AR)\r\n        Contabilidad bancaria (FI-BL)\r\n        Activos fijos (FI-AA)\r\n        Control presupuestario (FI-FM)\r\n        Gestión de viajes (FI-TV)\r\nCO-  Coordinación y control\r\n       Contabilidad de clases de costo (CO-OM-CEL)\r\n       Contabilidad de centros de costo (CO-OM-CCA)\r\n       Contabilidad de costos por procesos (CO-OM-ABC)\r\n       Órdenes CO (CO-OM-OPA)\r\n       Controlling de costos del producto (CO-PC)\r\n       Cuenta de resultados (CO-PA)\r\n       Contabilidad de centros de beneficio (EC-PCA)\r\nMM-Gestion de Materiales\r\n       Planificación de necesidades sobre consumo (MM-CBP)\r\n       Compras (MM-PUR)\r\n       Gestión de servicios (MM-SRV)\r\nSD - Módulo de ventas y distribución\r\n       Funciones básicas (SD-BF)\r\n       Ventas (SD-SLS)\r\n       Facturación (SD-BIL)\r\n       Comercio exterior / Aduanas (SD-FT)\r\nHR - Recursos humanos\r\n       Gestión de personal (PA)\r\n       Gestión de tiempos (PT)\r\n       Cálculo de nómina (PY)\r\nBI-   Business Intelligence\r\n       Data Warehousing\r\n       Inteligencia de negocios\r\n       Insight de negocios\r\n       Capacidades de gestión y medición\r\nPP - Módulo de Producción\r\nWF- WorkFlow\r\n\r\n \r\nABAP-  Programacion\r\nABAP es un lenguaje de programación orientada a objetos y tiene como características principales las siguientes: \r\n    - Está basado en la programación estructura\r\n    - Es un lenguaje interpretado y no compilado, aunque se pueden generar versiones compiladas \r\n    - Se utiliza para desarrollar dos tipos de programas: los que se ejecutan en modo reporting y los que se ejecutan en modo diálogo\r\n    - Existen sentencias que permiten conmutar entre los dos modos\r\n    - Está dirigido por eventos\r\n    -  Está integrado por completo en el sistema SAP R/3\r\n    -  Está preparado para que sea multi-idioma\r\n\r\n\r\nVersiones SAP\r\n2014 	SAP Business One 9.1 	Resources module\r\n2013 	SAP Business One 9.0 	Single Signon, Bin Locations\r\n2012 	SAP Business One 8.82 	Usability enchancements, SAP HANA Support\r\n2011 	SAP Business One 8.81 	Multiple Cost centers, Cockpit\r\n2010 	SAP Business One 8.8 	Crystal Reports integration, Unified codebase\r\n2008 	SAP Business One 2007 	New reconciliation engine\r\n2006 	SAP Business One 2005 	Enhanced usability and reporting, Enhanced SDK\r\n2004 	SAP Business One 2004 	Global Release, SAP Business One SDK\r\n', '2015-06-30'),
(465, 'Como implementar un formulario de login/registro de usuarios en PHP', 'PHP', '1. Formulario de registro\r\n\r\nEl formulario de registro debe recoger como mínimo el identificador de usuario y la contraseña que serán utilizadas posteriormente para la validación del usuario. Es muy habitual que se solicite también una dirección de correo y cada vez más la dirección de correo es utilizada como el identificador de usuario.\r\n\r\nAdicionalmente, el formulario puede solicitar información adicional: edad, sexo, dirección,… Algunos de estos datos pueden ser opcionales u obligatorios, dependiendo del tipo de servicio que ofrece el sitio web.\r\n\r\nPor ejemplo, el siguiente código HTML genera un formulario que solicita una dirección de email y contraseña, y ofrece la opción de suscribirse a un boletín de noticias:\r\n<form method="POST" action="/register.php" accept-charset="utf-8">\r\n  <label for="signup_email">Email</label>\r\n  <input type="text" name="signup_email" value="" id="signup_email" maxlength="255" size="30"  />\r\n  <label for="signup_password">Contraseña</label>\r\n  <input type="password" name="signup_password" value="" id="signup_password" size="30"  />\r\n  <label for="sconfirm_password">Confirmar Contraseña</label>\r\n  <input type="password" name="confirm_password" value="" id="signup_confirm_password" size="30"  />\r\n                   \r\n  <input type="checkbox" name="signup_newsletter" value="subscribed" checked="checked"\r\n                         id="signup_newsletter" class="chk-signup" />\r\n                  \r\n  <label for="signup_newsletter" class="txt-signup">Deseo recibir noticias, actualizaciones de\r\n                            software, y la última información relativa a productos y servicios.\r\n  </label>\r\n  <input type="submit" name="signup_submit" value="Registrarse" class="btn">\r\n</form>\r\n\r\nCon este código HTML (y el correspondiente código CSS), el formulario resultante es de la forma:\r\n\r\nformulario-de-registro\r\n2. Proceso del formulario de registro de usuario\r\n\r\nAl pulsar en el botón “Registrarse”, el contenido del formulario es enviado al script “register.php” para su proceso.\r\n\r\nPreviamente al envío, en un servicio en producción, el formulario incluye algún tipo de validación de los datos (Para verificar que la sintaxis de la dirección de email es correcta, el contenido de los campos “Contraseña” y “Confirmar contraseña coincide”, la contraseña tiene una longitud mínima, etc.) mediante una función javascript que se ejecuta asociada al evento “onsubmit”.\r\n\r\nDe todas maneras, es conveniente realizar también este tipo de validación en el servidor, en el script “register.php” que procesa el formulario.\r\n\r\nEste script puede tener la estructura siguiente:\r\n\r\n// Leer los datos del formulario \r\n // Realizar la validación de los datos. \r\n // Insertar el usuario en la base de datos...\r\n // Enviar un email de confirmación\r\n ...\r\n2.1. Leer los datos recibidos del formulario\r\n\r\nEl formulario de registro del ejemplo especifica method=”POST”. Por lo tanto, los valores de los campos del formulario son accesibles para el script PHP en forma de elementos del array $_POST:\r\n// Leer los datos del formulario\r\n$email = $_POST["signup_email"];\r\n$password = $_POST["signup_password"];\r\n$newsletter = $_POST["signup_newsletter"];\r\n2.2. Realizar la validación de los datos\r\n\r\nLa validación más elemental del formulario de registro incluye comprobar que la contraseña introducida tiene una longitud mínima, y que la contraseña de confirmación coincide. Si alguna de estas comprobaciones falla, se genera el correspondiente mensaje de error:\r\n// Realiza la validación\r\n$error_message = ""\r\nif (strlen($password) < 6) {\r\n    $error_message = "La contraseña es demasiado corta. Por favor, introduzca al menos 6 caracteres";\r\n} else if ( $password != $_POST["signup_confirm_password"]) {\r\n    $error_message = "Las contraseñas no coinciden. Por favor, inténtelo de nuevo";\r\n}\r\n\r\nPor otra parte, es conveniente comprobar que la sintaxis de la dirección de correo es correcta. Se puede utilizar la función PHP estándar filter_var() para realizar esta comprobación:\r\nif ( ! filter_var($email, FILTER_VALIDATE_EMAIL)) {\r\n    $error_message = "Por favor, compruebe la dirección de email introducida";\r\n}\r\n\r\nNota: filter_var() se puede utilizar también para comprobar la validez de otros tipos de datos, si se han incluido en el formulario, utilizando el tipo de validación correspondiente: FILTER_VALIDATE_BOOLEAN, FILTER_VALIDATE_INT, FILTER_VALIDATE_IP, FILTER_VALIDATE_URL, etc. La lista completa de tipos de filtros de validación disponibles se puede consultar aquí\r\n2.3. Insertar el usuario en la base  de datos\r\n\r\nLa aplicación debe almacenar la relación de usuarios registrados en alguna parte. En esta guía supondremos que se utiliza una tabla ‘usuarios’ en una base de datos mysql para contener los datos relativos a los usuarios registrados.\r\n\r\nLa tabla ‘usuarios’ incluye los siguientes campos:\r\n\r\n    `email`  – dirección de email. Este campo debe ser la clave primaria de la tabla, para evitar duplicados.\r\n    `password` – contraseña\r\n    `newsletter` – indica si el usuario desea (newsletter=1) o no (neswletter=0) recibir el boletín de noticias y otros emails del sitio web.\r\n    `registration_date` timestamp DEFAULT CURRENT_TIMESTAMP – fecha en la que se ha realizado el registro.\r\n    `activation_key` – clave de activación que se usa para confirmar la dirección de correo, como se explica en el siguiente apartado\r\n    `validated` – indica que la dirección de correo ha sido confirmada\r\n\r\nEl proceso de registro debe comprobar si la dirección de correo introducida existía previamente en la tabla de usuarios. Si el campo ‘email’ es la clave primaria de la tabla, esta comprobación puede realizarse en la sentencia de inserción del registro, porque la inserción fallará con un error de clave duplicada si el usuario estaba ya registrado.\r\n// Insertar usuario en la base de datos\r\n$random_key = generate_random_key();\r\n$stm = $dbh->prepare("insert into usuarios (email,password,activation_key,validated) " .\r\n                     "values ( ? , ? , ? , 0 )");\r\n$stm->bind_param("sss",$email,$password,$random_key);\r\nif (!$stm->execute()) {\r\n    // La inserción puede fallar si el usuario ya existía en la base de datos\r\n    $error_message = "Error, el usuario ya existía en la base de datos";\r\n} else {\r\n    // Enviar un mensaje para confirmar la dirección de correo del usuario\r\n    ...\r\n}\r\n\r\nLa función “generate_random_key()” genera una clave de activación aleatoria de 32 caracteres. Se puede implementar de la siguiente forma:\r\nfunction generate_random_key() {\r\n    $chars = "abcdefghijklmnopqrstuvwxyz0123456789";\r\n \r\n    $new_key = "";\r\n    for ($i = 0; $i < 32; $i++) {\r\n        $new_key .= $chars[rand(0,35)];\r\n    }\r\n    return $new_key;\r\n}\r\n2.4. Enviar un email de confirmación\r\n\r\nTras insertar el usuario en la tabla, se envía un mensaje de confirmación a la dirección de correo del mismo. El mensaje incluye un enlace sobre el que el usuario debe hacer click para confirmar el registro. Una posible implementación de este envío puede realizarse utilizando la función estándar de PHP mail():\r\n           // Enviar un mensaje para confirmar la dirección de email introducida\r\n            mail($email,"ejemplo.com - Activación de la cuenta",\r\n                "Bienvenido a ejemplo.com!\r\n         \r\n                Gracias por registrarse en nuestro sitio.\r\n                Su cuenta ha sido creada, y debe ser activada antes de poder ser utilizada.\r\n                Para activar la cuenta, haga click en el siguiente enlace o copielo en la\r\n                barra de direcciones del navegador:\r\n \r\nhttp://ejemplo.com/activate.php?activation=".$random_key);\r\n\r\nCuando el usuario recibe el mensaje y hace click sobre el enlace, en el servidor se ejecuta el script “activate.php”. El script recibe la clave de activación que ha sido pasada en la url como el valor del argumento “activation”.\r\n\r\nLa misión principal del script activate.php es poner a uno el valor del campo “validated” en la tabla de usuarios. A continuación, inicia una sesión con el nuevo usuario y presenta un mensaje de confirmación:\r\n$activation_key = $_GET["activation"];\r\n$dbh = get_dbh();\r\n// Busca la entrada en la tabla de usuarios para la clave de activación recibida\r\n$stm = $dbh->prepare("select count(1) from users where activation_key=?");\r\n$stm->bind_param("s",$activation_key);\r\n$stm->bind_result($total);\r\n$message = "";\r\n$stm->execute();\r\n$stm->fetch();\r\n$stm->close();\r\nif ($total == 1) { // Si se ha encontrado...\r\n    // Retrieve the email address\r\n    $stm = $dbh->prepare("select email from users where activation_key=?");\r\n    $stm->bind_param("s",$activation_key);\r\n    $stm->bind_result($email);\r\n    $stm->execute();\r\n    $stm->fetch();\r\n    $stm->close();\r\n    // Poner a uno el campo validated en la tabla usuarios\r\n    $stm = $dbh->prepare("update usuarios set validated=1 where activation_key=?");\r\n    $stm->bind_param("s",$activation_key);\r\n    $stm->execute();\r\n    $stm->close();\r\n    // Introducir al usuario en sesión\r\n    $_SESSION["user"] = $email;\r\n    $message .= "Gracias por registrarse con nosotros<br/><br/>" .\r\n        "¡Bienvenido a ejemplo.com!<br/><br/>" .\r\n        "<a href=''http://ejemplo.com'' class=\\"btn\\"''>Continuar</a>";\r\n\r\nY con esto finaliza el proceso de registro\r\n3. Formulario de login\r\n\r\nEl formulario de login contiene simplemente un campo “email” y un campo “clave”, que son enviados a un script “login.php”. Es habitual incluir también en el mismo un enlace a la funcionalidad de “Olvidó su contraseña”, como se puede ver en el siguiente ejemplo:\r\n<form method="POST" action="/user/login.php">\r\n    <label class="email">Email</label>\r\n    <input type="text" name="user">\r\n    <label class="pww">Clave</label>\r\n    <input type="password" name="pass">\r\n    <a href="/olvido.php" role="button" data-toggle="modal">¿Olvidó su contraseña?</a>\r\n    <input type="submit" class="btn" name="login" value="login">\r\n</form>\r\n\r\nCon este código html y una hoja de estilo CSS, podemos conseguir que el formulario tenga el siguiente aspecto:\r\n\r\nEl script “login.php” debe confirmar que la entrada correspondiente a la dirección de correo y contraseña introducidas existe en la tabla de usuarios, y si es así introducir al usuario en sesión:\r\n//Validar usuario y contraseña\r\n$email = $_POST["user"];\r\n$password = $_POST["pass"];\r\n \r\n$stm = $dbh->prepare("select email from users where email=? and password=?");\r\n$stm->bind_param("ss",$email,$password);\r\n$stm->execute();\r\n$login_error_message = "";\r\nif ($stm->fetch()) {\r\n    $_SESSION["user"] = $email;\r\n    // Redirigir al usuario a la página de inicio del sitio\r\n    header( ''Location: http://ejemplo.com/'' );\r\n    return;\r\n} else {\r\n    // Presentar mensaje de error\r\n}', '2015-06-30'),
(466, 'Conexion Access con PHP con Clase', 'PHP', '\r\n\r\n<?php\r\n/* CLASE PARA LA CONEXION DE PHP CON ACCES 2003 */\r\nclass database {\r\n # variable para almacenar la conexion\r\n private $conexion;  \r\n #Base de datos access 2003\r\n private  $name = ''test.mdb'';\r\n\r\n    /* METODO PARA CONECTAR CON LA BASE DE DATOS*/\r\n public function conectar()\r\n {\r\n    # Directorio actual de la base de datos\r\n    $db = getcwd()."\\\\".$this->name;\r\n    if( is_file($db) )\r\n    {\r\n     # Se forma la cadena de conexión\r\n     $dsn = "DRIVER={Microsoft Access Driver (*.mdb)};DBQ=".$db;\r\n    # Se realiza la conexón con Access\r\n    $this->conexion = odbc_connect( $dsn, '''', '''' );\r\n    if (!$this->conexion) \r\n     exit( "Error: No se pudo completar la conexion ");  \r\n     else      \r\n        echo ''Conexión a [''.$this->name.'' ]: Establecida'';\r\n    }    \r\n    else exit("Error: No existe archivo ".$this->name);\r\n }\r\n\r\n /* METODO PARA CERRAR LA CONEXION A LA BASE DE DATOS*/ \r\n public function desconectar()\r\n {\r\n  odbc_close( $this->conexion );\r\n  echo ''Conexion a [''.$this->name.''] : Terminado '';\r\n }\r\n\r\n}//fin clase\r\n\r\n#_______________________Soy una barra separadora :) \r\n\r\n #se crea instancia a clase \r\n $db = new database(); \r\n $db->conectar();\r\n echo ''<br/>''; \r\n $db->desconectar();\r\n?>\r\n\r\n', '2015-06-30'),
(467, 'msaccess.class.php Conectarse a Acces desde PHP', 'PHP', 'En este tuto crearemos una clase para conectarse con una base de datos Access y ademas se implementan dos metodos para realizar consultas e insertar registros desde PHP.\r\n\r\nAntes de comenzar con PHP debemos tener una base de datos.\r\n\r\nNecesitamos crear una base de datos en Acces con extensión *.mdb, llama a esta base de datos “demo.mdb”, debes añadir una tabla a la base de datos (Nombre tabla: tabla), y a esta tabla añades 2 campos de tipo String (Campo 1: Nombre, Campo 2: Correo), añade algunos valores. Este archivo MDB debe estar en la misma carpeta que los archivos php que crearemos.\r\n\r\n1. Crea un nuevo archivo PHP llamado “msaccess.class.php“, el código de la clase es:\r\n\r\n<?php\r\n/* CLASE PARA LA CONEXION DE PHP CON ACCES 2003 */\r\nclass msaccess {\r\n # variable para almacenar la conexion\r\n private $conexion;  \r\n #Base de datos access 2003\r\n private  $name = ''demo.mdb'';\r\n\r\n    /* METODO PARA CONECTAR CON LA BASE DE DATOS*/\r\n public function conectar()\r\n {\r\n    # Directorio actual de la base de datos\r\n    $db = getcwd()."\\\\".$this->name;\r\n    # Se forma la cadena de conexión\r\n    $dsn = "DRIVER={Microsoft Access Driver (*.mdb)};DBQ=".$db;\r\n    # Se realiza la conexón con Access -> Si hay error se detiene todo\r\n    $this->conexion = odbc_connect( $dsn, '''', '''' ) or die( odbc_errormsg() );\r\n     echo ''Conexión a [''.$this->name.'' ]: Establecida'';\r\n }\r\n\r\n /* METODO PARA CERRAR LA CONEXION A LA BASE DE DATOS*/ \r\n public function desconectar()\r\n {\r\n  odbc_close( $this->conexion );\r\n  echo ''Conexion a [''.$this->name.''] : Terminado '';\r\n }\r\n\r\n /*METODO PARA HACER UN INSERT\r\n * INPUT: \r\n * $tabla -> Nombre tabla \r\n * $campos -> String con nombres de los campos -> campo1, campo2, campo_n\r\n * $valores -> Valores a insertar -> ''Valor1'',''Valor2'',''Valor_n''\r\n * OUTPUT:\r\n * boolean -> TRUE/FALSE: \r\n */\r\n function insert($tabla, $campos, $valores){\r\n  #se forma la instruccion SQL \r\n  $q = ''INSERT INTO ''.$tabla.'' (''.$campos.'') VALUES (''.$valores.'')'';\r\n  $resultado = $this->consulta($q);\r\n  if($resultado) return true;\r\n  else return false;\r\n }   \r\n\r\n /* METODO PARA REALIZAR UNA CONSULTA \r\n * INPUT: \r\n * $q -> consulta SQL\r\n *OUTPUT: \r\n * $result\r\n */\r\n public function consulta($q)\r\n {\r\n    $resultado = odbc_exec( $this->conexion, $q) or die( odbc_errormsg() );\r\n    return $resultado; \r\n }\r\n\r\n}//fin clase\r\n?>\r\n\r\nEsta clase implementa los metodos para la conexión con Acces mediante el uso de ODBC y los metodos de consulta e insercción de filas. Esta comentado al detalle creo y puede ser facilmente entendible. Continuemos.\r\n\r\n2. Ahora que tenemos nuestra clase de conexion con acces, debemos implementarla, crea un nuevo archivo php, “index.php” y coloca el siguiente código:\r\n\r\n<?php\r\n require ''msaccess.class.php'';\r\n $db = new msaccess();\r\n\r\n $db->conectar();\r\n echo ''<br/>''; \r\n #Se realiza consulta\r\n $rs = $db->consulta( " SELECT * FROM tabla" );\r\n #Se imprimen los datos\r\n echo " Consulta: <br/>";\r\n while ( odbc_fetch_row($rs) ) \r\n      echo ''- ''.odbc_result($rs,"Nombre").'' | '' .odbc_result($rs,"Correo").'' |<br/>'';\r\n #Se agrega un nuevo registro\r\n echo "Se agrega nuevo registro: <br/>";\r\n $db->insert("tabla" , " Nombre, Correo " , " ''Rasputia'',''rp_678tia@hotmail.com'' " );\r\n #Se realiza consulta \r\n $rs = $db->consulta( " SELECT * FROM tabla" );\r\n #Se imprimen los datos \r\n while ( odbc_fetch_row($rs) ) \r\n      echo ''- ''.odbc_result($rs,"Nombre").'' | '' .odbc_result($rs,"Correo").'' |<br/>''; \r\n $db->desconectar(); \r\n?>\r\n\r\n', '2015-06-30'),
(468, 'Ciclo de Ventas', 'SAP', '    [VENTAS CLIENTES]\r\n     Oferta de venta\r\n     Ventas Clientes-> Oferta de venta\r\n     Llenamos los datos de la cotizacion  \r\n     boton inferior  -> Copia a pedido Cliente “Orden de Compra”.\r\n     Llenamos la fecha de entrega\r\n     Crear\r\n     Control + Flecha Izquierda  \r\n     “Vamos a la orden de compra anterior”\r\n     boton inferior  -> Copia a “Entrega”.\r\n     boton inferior  -> Copia a “Factura de deudores”.', '2015-06-30'),
(469, 'Seguridad en Secciones ', 'PHP', 'ini_set(''session.cookie_secure'',1);\r\nini_set(''session.cookie_httponly'',1);\r\nini_set(''session.use_only_cookies'',1);\r\nsession_start();', '2015-06-30');
